DROP TABLE IF EXISTS `articletable`;
CREATE TABLE `articletable` (
  `article_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `article_time` varchar(50) NOT NULL,
  `topic_id` int(11) NOT NULL,
  `article_content` text NOT NULL,
  `article_title` varchar(50) NOT NULL,
  `article_img` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`article_id`),
  KEY `fk_article_user` (`user_id`),
  KEY `fk_article_topic` (`topic_id`),
  CONSTRAINT `fk_article_topic` FOREIGN KEY (`topic_id`) REFERENCES `topictable` (`topic_id`),
  CONSTRAINT `fk_article_user` FOREIGN KEY (`user_id`) REFERENCES `usertable` (`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=133 DEFAULT CHARSET=utf8;

