-- ----------------------------
-- Table structure for artrecomtable
-- ----------------------------
DROP TABLE IF EXISTS `artrecomtable`;
CREATE TABLE `artrecomtable` (
  `artrecom_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `recom_content` varchar(100) NOT NULL,
  `recom_time` varchar(20) NOT NULL,
  `articlecom_id` int(11) NOT NULL,
  `neww` int(2) NOT NULL,
  PRIMARY KEY (`artrecom_id`),
  KEY `fk_art_com` (`articlecom_id`),
  KEY `fk_art_user` (`user_id`),
  CONSTRAINT `fk_art_com` FOREIGN KEY (`articlecom_id`) REFERENCES `articlecomtable` (`articlecom_id`),
  CONSTRAINT `fk_art_user` FOREIGN KEY (`user_id`) REFERENCES `usertable` (`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=27 DEFAULT CHARSET=utf8;

