-- ----------------------------
-- Table structure for attenttable
-- ----------------------------
DROP TABLE IF EXISTS `attenttable`;
CREATE TABLE `attenttable` (
  `attent_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `topic_id` int(11) NOT NULL,
  `attent_time` varchar(50) NOT NULL,
  `attent_if` int(1) NOT NULL DEFAULT '1',
  PRIMARY KEY (`attent_id`),
  KEY `fk_attent_user` (`user_id`),
  KEY `fk_attent_topic` (`topic_id`),
  CONSTRAINT `fk_attent_topic` FOREIGN KEY (`topic_id`) REFERENCES `topictable` (`topic_id`),
  CONSTRAINT `fk_attent_user` FOREIGN KEY (`user_id`) REFERENCES `usertable` (`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=359 DEFAULT CHARSET=utf8;

