-- ----------------------------
-- Table structure for beautytable
-- ----------------------------
DROP TABLE IF EXISTS `beautytable`;
CREATE TABLE `beautytable` (
  `beauty_id` int(11) NOT NULL AUTO_INCREMENT,
  `beauty_content` text,
  `book_id` int(11) DEFAULT NULL,
  PRIMARY KEY (`beauty_id`),
  KEY `fk_beauty_book` (`book_id`),
  CONSTRAINT `fk_beauty_book` FOREIGN KEY (`book_id`) REFERENCES `booktable` (`book_id`)
) ENGINE=InnoDB AUTO_INCREMENT=134 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of beautytable
-- ----------------------------
INSERT INTO `beautytable` VALUES ('1', '在某种情况下，一个人的存在本身就要伤害另一个人。', '3');
INSERT INTO `beautytable` VALUES ('2', '可是，在你生命里，却是不如意十之八九百千万.', '3');
INSERT INTO `beautytable` VALUES ('3', '没有什么比时间更具有说服力了，因为时间无需通知我们就可以改变一切。', '80');
INSERT INTO `beautytable` VALUES ('4', '以笑的方式哭，在死亡的伴随下活着。', '80');
INSERT INTO `beautytable` VALUES ('5', '最初我们来到这个世界，是因为不得不来；最终我们离开这个世界，是因为不得不走。', '80');
INSERT INTO `beautytable` VALUES ('6', '事情都是被逼出来的，人只有被逼上绝路了，才会有办法，没上绝路以前，不是没想到办法，就是想到了也不知道该不该去做。', '81');
INSERT INTO `beautytable` VALUES ('7', '爱不是我多有钱，有多么大的智慧和成就，而是我把一切给你。关键时刻，替你挡风遮雨', '81');
INSERT INTO `beautytable` VALUES ('8', '我的悲伤还来不及出发，就已经到站下车。', '82');
INSERT INTO `beautytable` VALUES ('9', '如果你的世界，没有痛苦的害怕，没有尊严的担忧，没有富贵的贫贱，没有暖寒的交替，没有外貌的困扰，没有男女的区别，没有你我之分，没有生死顾虑，你才会离＂真正的活着＂越来越近。', '82');
INSERT INTO `beautytable` VALUES ('10', '从来如此，便对么？', '83');
INSERT INTO `beautytable` VALUES ('11', '我翻开历史一查，这历史没有年代。歪歪斜斜的每页上都写着“仁义道德”几个字，我横竖睡不着，仔细看了半夜，才从字缝里看出来，满本上都写着两个字“吃人\"！', '83');
INSERT INTO `beautytable` VALUES ('12', '凡事总需研究，才会明白。', '83');
INSERT INTO `beautytable` VALUES ('13', '一个人做到只剩了回忆的时候，生涯大概总要算是无聊了吧，但有时竟会连回忆也没有。', '84');
INSERT INTO `beautytable` VALUES ('14', '在中国的天地间，不但做人，便是做鬼，也艰难极了。', '84');
INSERT INTO `beautytable` VALUES ('15', '想到生的乐趣，生固然可以留恋；但想到生的苦趣，无常也不一定是恶客。', '84');
INSERT INTO `beautytable` VALUES ('16', '一个人可以被毁灭，但不能被打败。', '85');
INSERT INTO `beautytable` VALUES ('17', '现在不是去想缺少什么的时候，该想一想凭现有的东西你能做什么。', '85');
INSERT INTO `beautytable` VALUES ('18', '一个人并不是生来要给打败的，你尽可以把他消灭掉，可就是打不败他。', '85');
INSERT INTO `beautytable` VALUES ('19', '等待也是种信念 <br>\r\n海的爱太深，时间太浅 <br>\r\n秋天的夜凋零在漫天落叶里面，泛黄世界一点一点随风而渐远', '85');
INSERT INTO `beautytable` VALUES ('20', '决不要同你并不爱的人一起出门旅行。 不要搞任何花招去迎接任何一种俗套。', '86');
INSERT INTO `beautytable` VALUES ('21', '假如你有幸年轻时在巴黎生活过，那么你此后一生中不论去到哪里她都与你同在，因为巴黎是一席流动的盛宴。', '86');
INSERT INTO `beautytable` VALUES ('22', '如果你什么都做得太久，开始得太晚，你就不指望大家还留在那里。人都散了。', '87');
INSERT INTO `beautytable` VALUES ('23', '如果他以谎言为生，他就应该试着以谎言而死。', '87');
INSERT INTO `beautytable` VALUES ('24', '无论你成就如何，你的生活就是你的天赋所在。', '87');
INSERT INTO `beautytable` VALUES ('25', '生活总是让我们遍体鳞伤，但到后来，那些受伤的地方一定会变成我们最强壮的地方。', '88');
INSERT INTO `beautytable` VALUES ('26', '相爱的人不该争吵。因为他们只有两人，与他们作对的是整个世界。他们一发生隔膜，世界就会将其征服。', '88');
INSERT INTO `beautytable` VALUES ('27', '男女之间虽然相爱，却时常想要单独静一下，而一分开，必然招来对方猜忌。', '88');
INSERT INTO `beautytable` VALUES ('28', '狡兔死，走狗烹；飞鸟尽，良弓藏；敌国破，谋臣亡', '90');
INSERT INTO `beautytable` VALUES ('29', '何谓君子？何谓小人？德大于才便是君子，才大于德便是小人。', '90');
INSERT INTO `beautytable` VALUES ('30', '二十八打天下，戎马一生,驰骋千里,只为梦萦', '90');
INSERT INTO `beautytable` VALUES ('31', '运筹帷幄之中，决胜千里之外。', '91');
INSERT INTO `beautytable` VALUES ('32', '顺 不妄喜；逆 不遑馁；安 不奢逸；危 不惊惧；胸有惊雷而如平湖者，可拜上将军也。', '91');
INSERT INTO `beautytable` VALUES ('33', '王侯将相宁有种乎？', '91');
INSERT INTO `beautytable` VALUES ('34', '你现在成功了，人家才会重视你的过去。如果你现在什么也不是，那么你的过去就是一堆垃圾。', '92');
INSERT INTO `beautytable` VALUES ('35', '权术并不是不能用，但若是一个人没有大韬略、大格局、大想法，那么，权术用多了，可能就会适得其反。', '92');
INSERT INTO `beautytable` VALUES ('36', '人性，在历史上很难有变化，几千年来估计都是这样。通过正规渠道上不去，那就走邪道，只要能出名就行。', '92');
INSERT INTO `beautytable` VALUES ('37', '所谓“德性”，这个“德”就是指的道德，所谓“天命有常，惟有德者居之”，可见咱们中国人几千年前就在讲以德治国了。至于“性”，指的是属性。德性就是道德的属性，国家政权的属性。', '93');
INSERT INTO `beautytable` VALUES ('38', '历代王朝的德性是一件无聊而重要的大事，如何把一件无聊的事尽量说得有意思又保证精准翔实，是一项很有挑战性的工作。', '93');
INSERT INTO `beautytable` VALUES ('39', '所谓“有效”的减肥食谱，具体要这样衡量----肥胖者不应感到饥饿，应当减轻疲劳感，减轻适当的体重，且该食谱需适用于长期体重控制和调理。', '95');
INSERT INTO `beautytable` VALUES ('40', '肥胖归根结底是由激素失衡，而不是卡路里失衡造成的。具体而言就是，使用易消化、富含糖类的食物会刺激胰岛素分泌。', '95');
INSERT INTO `beautytable` VALUES ('41', '每一种疾病都是一种表达，当我们压抑一些东西，不允许它在心理和灵性层面表达时，它会通过身体而表达，这就是身体的疾病。', '96');
INSERT INTO `beautytable` VALUES ('42', '如果你想要任何东西，你要先把这个东西给出去，给别人。＂给别人＂是种子，种子会开花结果。', '96');
INSERT INTO `beautytable` VALUES ('43', '“想要一边吃喜欢的食物一边减肥”“不想改变饮食习惯”“虽然知道吃太多不好，但还是控制不住要吃”。<br>\r\n这些人都是因为不愿改变饮食习惯，所以才选择运动减肥，会有上述的想法也是理所当然的。', '98');
INSERT INTO `beautytable` VALUES ('44', '用冥想清除对食物的渴望。冥想通过调整身体本能的战或逃反应，诱导这个反应的反面过程——放松反应——来使这种反应缓和下来，减慢心率和呼吸、降低血压、放松肌肉。你的意识像湖，当你的意识平静和清澈时，你就能看到湖底。', '99');
INSERT INTO `beautytable` VALUES ('45', '哪里会有人喜欢孤独，不过是不喜欢失望罢了。', '1');
INSERT INTO `beautytable` VALUES ('46', '希望你可以记住我，记住我这样活过，这样在你身边呆过。', '1');
INSERT INTO `beautytable` VALUES ('47', '“最最喜欢你，绿子。” <br>\r\n“什么程度？” <br>\r\n“像喜欢春天的熊一样。”\r\n“我就这么喜欢你。”', '1');
INSERT INTO `beautytable` VALUES ('48', '每个人都有属于自己的一片森林，也许我们 从来不曾去过，但它一直在那里，总会在那里。迷失的人迷失了，相逢的人会再相逢。', '1');
INSERT INTO `beautytable` VALUES ('49', '不存在十全十美的文章，如同不存在彻头彻尾的绝望。', '2');
INSERT INTO `beautytable` VALUES ('50', '看海看久了想见人，见人见多了想看海。', '2');
INSERT INTO `beautytable` VALUES ('51', '我们许久许久地缄默不语，只是一味地望着海面望着天空望着船口，晚风掠过海面而拂动草丛的时间里，暮色渐渐变成淡淡的夜色，几颗银星开始在船坞上方闪闪眨眼。', '2');
INSERT INTO `beautytable` VALUES ('52', '我们是在时间之中彷惶，从宇宙诞生直到死亡的时间里。所以我们无所谓生也无所谓死。只是风。', '2');
INSERT INTO `beautytable` VALUES ('53', '刚刚好，看见你幸福的样子，于是幸福着你的幸福。', '3');
INSERT INTO `beautytable` VALUES ('54', '没有谁是一座孤岛，每本书都是一个世界。', '4');
INSERT INTO `beautytable` VALUES ('55', '没有人会漫无目的地旅行，那些迷路者是希望迷路。', '4');
INSERT INTO `beautytable` VALUES ('56', '一旦一个人在乎一件事，就发现自己不得不开始在乎一切事。', '4');
INSERT INTO `beautytable` VALUES ('57', '死亡是唯一一座永远亮着的灯塔，不管你向哪里航行，最终都得转向它指引的方向。一切都会逝去，只有死神永生。', '5');
INSERT INTO `beautytable` VALUES ('58', '失去人性，失去很多；失去兽性，失去一切。', '5');
INSERT INTO `beautytable` VALUES ('59', '一队蚂蚁不停搬运米粒大小的石块,给它们十亿年,就能把泰山搬走。只要把时间拉得足够长,生命比岩石和金属都强壮得多,比飓风和火山更有力。', '5');
INSERT INTO `beautytable` VALUES ('60', '你穿过城市，走遍街道，朝你的自由飞奔;而我，朝你走过去，我并不知道也不了解，究竟是什么力量将我推向前方。', '71');
INSERT INTO `beautytable` VALUES ('61', '让我们从永恒中偷几个小时过来，一起分享那些我们没谈过的事。正是为了这个，我才从另一个世界回来。', '71');
INSERT INTO `beautytable` VALUES ('62', '我的眼睛里有你微笑的颜色，谢谢所有这些微笑，谢谢所有这些温情。我要你活下去，当我不在的时候，你要重新恢复你的生活。', '72');
INSERT INTO `beautytable` VALUES ('63', '爱的风险，在于既爱优点又爱缺点，它们是不可分割的。', '72');
INSERT INTO `beautytable` VALUES ('64', '你偷走了我的影子，不论你在哪里，我都会一直想着你。', '73');
INSERT INTO `beautytable` VALUES ('65', '人一旦开始撒了一个谎，就再也不知道如何停止。', '73');
INSERT INTO `beautytable` VALUES ('66', '其实最棒的回忆就在当下，在眼前，而且这会是人生最美好的时光。', '73');
INSERT INTO `beautytable` VALUES ('67', '没有任何的牢笼可禁锢我们的思想，所有情愫都可以穿越栅栏飞向远方。', '74');
INSERT INTO `beautytable` VALUES ('68', '各种形式下的爱情，这是我们在这个并不美好的世界里活下去的唯一理由。', '74');
INSERT INTO `beautytable` VALUES ('69', '一朝春尽红颜老，花落人亡两不知。', '75');
INSERT INTO `beautytable` VALUES ('70', '假作真时真亦假，无为有处有还无。', '75');
INSERT INTO `beautytable` VALUES ('71', '世事洞明皆学问，人情练达即文章。', '75');
INSERT INTO `beautytable` VALUES ('72', '满纸荒唐言，一把辛酸泪。都云作者痴，谁解其中味？', '75');
INSERT INTO `beautytable` VALUES ('73', '大丈夫生居天地间，岂能郁郁久居人下！', '76');
INSERT INTO `beautytable` VALUES ('74', '玉可碎而不可改其白，竹可焚而不可毁其节。', '76');
INSERT INTO `beautytable` VALUES ('75', '大丈夫生于乱世，当带三尺剑立不世之功；今所志未遂，奈何死乎！', '76');
INSERT INTO `beautytable` VALUES ('76', '良禽择木而栖，贤臣择主而事。见机不早，悔之晚矣。', '76');
INSERT INTO `beautytable` VALUES ('77', '他日若遂凌云志，敢笑黄巢不丈夫', '77');
INSERT INTO `beautytable` VALUES ('78', '有缘千里来相会，无缘对面不相逢。', '77');
INSERT INTO `beautytable` VALUES ('79', '莫语常言道知足，万事至终总是空。理想现实一线隔，心无旁骛脚踏实。 谁无暴风劲雨时，守得云开见月明。花开复见却飘零，残憾莫使今生留。', '77');
INSERT INTO `beautytable` VALUES ('80', '一叶浮萍归大海，人生何处不相逢', '78');
INSERT INTO `beautytable` VALUES ('81', '心生，种种魔生:心灭，种种魔灭。', '78');
INSERT INTO `beautytable` VALUES ('82', '人情似纸张张薄，世事如棋局局新。贫居闹市无人问，富在深山有远亲。不信但看宴中酒，杯杯先敬富贵人。', '78');
INSERT INTO `beautytable` VALUES ('83', '情之所钟，虽丑不嫌。', '79');
INSERT INTO `beautytable` VALUES ('84', '你的过去我来不及参与，你的未来我不会再错过。<br>\r\n归纳起来不外乎四个字：<br>\r\n相见恨晚。', '79');
INSERT INTO `beautytable` VALUES ('85', '世事茫茫，光阴有限，算来何必奔忙！人生碌碌，竞短论长，却不道荣枯有数，得失难量。', '79');
INSERT INTO `beautytable` VALUES ('86', '对那些愚蠢的男人来说，她的确聪明过头了，夫人。但是谁会想和他们在一起？一个愚蠢的男人会毁了每一个女人。', '6');
INSERT INTO `beautytable` VALUES ('87', '她指了指女儿：“我这个奇怪的孩子想在21岁前读完所有的书。我说好吧，可以给她买这些百科……百科……哦，就是这些参考书。但是她以后就没有生日礼物了，也没有圣诞礼物了。”', '6');
INSERT INTO `beautytable` VALUES ('88', '错误的人什么都没有留下，只教会了我们惩罚对的人的方法。比如一段突如其来的倔强，比如一场没有终结的冷漠，比如一次轻而易举的离开。', '56');
INSERT INTO `beautytable` VALUES ('89', '一定要笑，无论有多绝望。', '56');
INSERT INTO `beautytable` VALUES ('90', '有时候我独自在家，一个人练习我们其乐融融的样子。', '56');
INSERT INTO `beautytable` VALUES ('91', '请相信，这个世界上真的有人过着你想要的生活。忽晴忽雨的江湖，祝你有梦为马，随处可栖。', '57');
INSERT INTO `beautytable` VALUES ('92', '其实世上哪儿有什么一见钟情，所谓的一见钟情，不过是你终于遇到了你那个一直想要的人而已。人海茫茫，遇之是幸，不遇是命。', '57');
INSERT INTO `beautytable` VALUES ('93', '没人告诉过我，很多人一辈子只能遇见一次，擦肩而过就是杳然一生', '57');
INSERT INTO `beautytable` VALUES ('94', '你我都明白，这从来就不是公平的世界。<br> \r\n人们起点不同，路径不同，乃至遭遇不同，命运不同。有人认命，有人顺命，有人抗命，有人玩命，希望和失望交错而生，倏尔一生。', '57');
INSERT INTO `beautytable` VALUES ('95', '我最想去的地方，是你的心里，我的心不大，只装得下一个你。', '58');
INSERT INTO `beautytable` VALUES ('96', '尽管你还没有嫁衣，但在我心中，你已经是我的新娘。', '58');
INSERT INTO `beautytable` VALUES ('97', '我的心不大，只装得下一个你。', '58');
INSERT INTO `beautytable` VALUES ('98', '我希望有个如你一般的人 <br>\r\n如山间清爽的风，\r\n如古城温暖的光 <br>\r\n从清晨到夜晚，\r\n由山野到书房 <br>\r\n只要最后是你 <br>\r\n就好', '59');
INSERT INTO `beautytable` VALUES ('99', '在季节的车上，如果你要提前下车，请别推醒装睡的我，这样我可以沉睡到终点，假装不知道你已经离开.', '59');
INSERT INTO `beautytable` VALUES ('100', '一个人的记忆就是座城市，时间腐蚀着一切建筑，把高楼和道路全部沙化。如果你不往前走，就会被沙子掩埋。所以我们泪流满面，步步回头，可是只能往前走。', '59');
INSERT INTO `beautytable` VALUES ('101', '我若不喜欢你，怎会和你做朋友？我若喜欢你，怎会仅仅与你做朋友？', '60');
INSERT INTO `beautytable` VALUES ('102', '有两种不联系：一种是忘记了，一种是放在回忆里。', '60');
INSERT INTO `beautytable` VALUES ('103', '\"我很好\"是告诉他们，你越来越能接受现实，而不是越来越现实。', '60');
INSERT INTO `beautytable` VALUES ('104', '给时光以生命，给岁月以文明。', '61');
INSERT INTO `beautytable` VALUES ('105', '你的无畏来源于无知。', '61');
INSERT INTO `beautytable` VALUES ('106', '我们都是阴沟里的虫子,但总还是得有人仰望星空.', '61');
INSERT INTO `beautytable` VALUES ('107', '在中国，任何超脱飞扬的思想都会砰然坠地——现实的引力实在是太沉重了。', '62');
INSERT INTO `beautytable` VALUES ('108', '死亡是一座永恒的灯塔，不管你驶向何方，最终都会朝它转向。一切都将逝去，只有死神永生', '62');
INSERT INTO `beautytable` VALUES ('109', '碑是那么小，与其说是为了纪念，更像是为了忘却。', '62');
INSERT INTO `beautytable` VALUES ('110', '生命进化的趋势是向小的方向，大不等于伟大，微小的生命更能同大自然保持和谐。', '63');
INSERT INTO `beautytable` VALUES ('111', '他看到有了一个黑白相间的地球。<br>\r\n黑色是溶化后又冷却的岩石，那是墓碑的黑色：白色是蒸发后又冻结的海洋，那是殓布的白色。', '63');
INSERT INTO `beautytable` VALUES ('112', '大不等于强大。', '63');
INSERT INTO `beautytable` VALUES ('113', '基延是基因改造延长生命技术的简称，通过去除人类基因中产生衰老时钟的片段，可将人类的正常寿命延长至三百岁。', '64');
INSERT INTO `beautytable` VALUES ('114', '对穷人的同情，关键在于一个同字，当双方相同的物种基础不存在时，同情也就不存在了。这是人类的第二次进化，第一次与猿分开，靠的是自然选择；这一次与穷人分开来，靠的是另一条同样神圣的法则：私有财产不可侵犯。', '64');
INSERT INTO `beautytable` VALUES ('115', '没有命运，我们创造命运！', '64');
INSERT INTO `beautytable` VALUES ('116', '有时候，一个人只要好好活着，就足以拯救某个人。', '65');
INSERT INTO `beautytable` VALUES ('117', '如果你过的不幸福，我所做的一切才是徒劳', '65');
INSERT INTO `beautytable` VALUES ('118', '这个世上没有无用的齿轮，也只有齿轮本身能决定自己的用途。', '65');
INSERT INTO `beautytable` VALUES ('119', '他的工作，是扯下对手的面具；<br> 她的工作，是保护客人的面具。<br>戴好你的面具，千万不要被识破！', '66');
INSERT INTO `beautytable` VALUES ('120', '本来结婚典礼是象征幸福的仪式，但也只不过是举办仪式的当事人的幸福而已，并不是所有祝福都是发自肺腑的。因为，一生的伴侣既已选定，其他的人也就必然没有了机会。', '66');
INSERT INTO `beautytable` VALUES ('121', '精致的沉默，凌驾于一切之上', '66');
INSERT INTO `beautytable` VALUES ('122', '两袖一挥，清风明月；仰天一笑，快意平生；布履一双，山河踏遍；心有明珠，山河明媚。', '67');
INSERT INTO `beautytable` VALUES ('123', '琉璃珠，萤月美，恍若墨香指笔绘。那一日，西子湖畔，你眸眼似墨笑春风，而我未曾走进你的视线。', '67');
INSERT INTO `beautytable` VALUES ('124', '西子湖畔西子情，楼外楼中雨霖铃。<br> \r\n画廊绣舫霓裳舞，小桥流水叶聘婷。', '67');
INSERT INTO `beautytable` VALUES ('125', '无论凶手事后如何反省，多么后悔，死去的生命都无法复活。', '68');
INSERT INTO `beautytable` VALUES ('126', '要求杀人凶手自我惩戒，根本就是虚无的十字架，即使是这种虚无的十字架，也必须让凶手在监狱中背负着', '68');
INSERT INTO `beautytable` VALUES ('127', '到底有谁可以断言，“这个杀人凶手只要在监狱关多少多少年，就可以改写归正”，把杀人凶手绑在这种虚无的十字架上，到底有什么意义？', '68');
INSERT INTO `beautytable` VALUES ('128', '“这个世界的未来，究竟会怎么样？” <br>\r\n“我和你说，还是不要知道比较幸福。”', '69');
INSERT INTO `beautytable` VALUES ('129', '过去发生了什么，无人知晓，已经称不上是真相了。', '69');
INSERT INTO `beautytable` VALUES ('130', '大多数平庸之辈都是如此，默默消失，一点真相都没有留下。', '69');
INSERT INTO `beautytable` VALUES ('131', '此刻迈着轻快脚步的我对你还一无所知。你，注定会在未来的某一天，送给我生命中最美丽的礼物。', '70');
INSERT INTO `beautytable` VALUES ('132', '今天，我还不认识你，谁能想道，明天，我就将爱上你。', '70');
INSERT INTO `beautytable` VALUES ('133', '哪怕只是微不足道的肯定，对一个人来说，都是莫大的精神慰藉。', '70');

