-- ----------------------------
-- Table structure for booktable
-- ----------------------------
DROP TABLE IF EXISTS `booktable`;
CREATE TABLE `booktable` (
  `book_id` int(11) NOT NULL AUTO_INCREMENT,
  `book_name` varchar(20) NOT NULL,
  `book_subhead` varchar(20) DEFAULT NULL,
  `book_price` varchar(10) NOT NULL,
  `book_content` text,
  `writer_id` int(11) NOT NULL,
  `book_tag` varchar(20) NOT NULL,
  `book_catalog` varchar(100) DEFAULT NULL,
  `book_img` varchar(100) DEFAULT NULL,
  `book_year` varchar(20) DEFAULT NULL,
  PRIMARY KEY (`book_id`),
  KEY `fk_book_writer` (`writer_id`),
  CONSTRAINT `fk_book_writer` FOREIGN KEY (`writer_id`) REFERENCES `writertable` (`writer_id`)
) ENGINE=InnoDB AUTO_INCREMENT=105 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of booktable
-- ----------------------------
INSERT INTO `booktable` VALUES ('1', '挪威的森林', '村上春树文集', '22.50', '这是一部动人心弦的、平缓舒雅的、略带感伤的恋爱小说。小说主人公渡边以第一人称展开他同两个女孩间的爱<br/>情纠葛。渡边的第一个恋人直子原是他高中要好同学木月的女友，后来木月自杀了。一年后渡边同直子不期而遇并开<br/>始交往。此时的直子已变得娴静腼腆，美丽晶莹的眸子里不时掠过一丝难以捕捉的阴翳。两人只是日复一日地在落叶<br/>飘零的东京街头漫无目标地或前或后或并肩行走不止。直子20岁生日的晚上两人发生了性关系，不料第二天直子便不<br/>知去向。几个月后直子来信说她住进一家远在深山里的精神疗养院。渡边前去探望时发现直子开始带有成熟女性的丰<br/>腴与娇美。晚间两人虽同处一室，但渡边约束了自己，分手前表示永远等待直子。返校不久，由于一次偶然相遇，渡<br/>边开始与低年级的绿子交往。绿子同内向的直子截然相反，“简直就像迎着春天的晨光蹦跳到世界上来的一头小鹿”。这<br/>期间，渡边内心十分苦闷彷徨。一方面念念不忘直子缠绵的病情与柔情，一方面又难以抗拒绿子大胆的表白和迷人的<br/>活力。不久传来直子自杀的噩耗，渡边失魂魄地四处徒步旅行。最后，在直子同房病友玲子的鼓励下，开始摸索此后<br/>的人生。', '1', '小说/外国', null, 'book001.jpg', '2017-09-12');
INSERT INTO `booktable` VALUES ('2', '且听风吟', '村上春树文集', '24.50', '《且听风吟》是村上春树的成名作，他是以这部中篇开始文学创作的。<br/>\r\n小说的情节并不很复杂。“我”在酒吧喝酒，去卫生间时见一少女醉倒在地，遂就其护送回家，翌日少女发现自己一<br/>丝不挂，斥责“我”侮辱了她，“我”有口难辩。但几天后，两人逐渐亲密……不料“我”寒假回来，少女已无处可寻，只好<br/>一个人坐在原来两人坐过的地方怅怅地望着大海。\r\n《且听风吟》是村上春树的成名作，他是以这部中篇开始文学创作的。<br/>\r\n小说的情节并不很复杂。“我”在酒吧喝酒，去卫生间时见一少女醉倒在地，遂就其护送回家，翌日少女发现自己一<br/>丝不挂，斥责“我”侮辱了她，“我”有口难辩。但几天后，两人逐渐亲密……不料“我”寒假回来，少女已无处可寻，只好<br/>一个人坐在原来两人坐过的地方怅怅地望着大海。\r\n《且听风吟》是村上春树的成名作，他是以这部中篇开始文学创作的。<br/>小说的情节并不很复杂。“我”在酒吧喝酒，去卫生间时见一少女醉倒在地，遂就其护送回家，翌日少女发现自己一<br/>丝不挂，斥责“我”侮辱了她，“我”有口难辩。但几天后，两人逐渐亲密……不料“我”寒假回来，少女已无处可寻，只好<br/>一个人坐在原来两人坐过的地方怅怅地望着大海。', '1', '文学艺术/当代', null, 'book002.jpg', '2017-09-09');
INSERT INTO `booktable` VALUES ('3', '国境以南，太阳以西', '村上春树文集', '26.00', '37岁的男主人公，在东京市区拥有两家兴旺的酒吧，还有娇美的妻子，可爱的女儿，他是一位真正的成功人士。<br/>但是，他的内心还是感到饥饿干渴，事业和家庭都填补不了，而让他那缺憾的部分充盈起来的，是他小学时的女友岛<br/>本。岛本不愿吐露自己的经历、身份、只希望他就这样接受眼前的自己，只把她当成小学时那个爱古典乐的女孩。然<br/>而，就在他接受了这不可能接受的条件时，两人却在箱根别墅度过了销魂的一夜。翌晨，她一去杳然、再无踪迹可寻<br/>了。', '1', '成功励志/情商', null, 'book003.jpg', '2017-06-05');
INSERT INTO `booktable` VALUES ('4', '岛上书店', '读客全球顶级畅销小说文库：加·泽文作品', '24.10', '岛上书店是间维多利亚风格的小屋，门廊上挂着褪色的招牌，上面写着： 没有谁是一座孤岛，每本书都是一个世<br/>界 A．J．费克里，人近中年，在一座与世隔绝的小岛上，经营一家书店。 命运从未眷顾过他，爱妻去世，书店危机，<br/>就连唯一值钱的宝贝也遭窃。他的人生陷入僵局，他的内心沦为荒岛。 就在此时，一个神秘的包袱出现在书店中，意<br/>外地拯救了陷于孤独绝境中的A．J．，成为了连接他和小姨子伊斯梅、警长兰比亚斯、出版社女业务员阿米莉娅之间<br/>的纽带，为他的生活带来了转机。 小岛上的几个生命紧紧相依，走出了人生的困境，而所有对书和生活的热爱都周而<br/>复始，愈加汹涌。', '6', '人文社科/历史', null, 'book004.jpg', '2017-09-12');
INSERT INTO `booktable` VALUES ('5', '三体III', '死神永生', '38.00', '与三体文明的战争使人类第一次看到了宇宙黑暗的真相，地球文明像一个恐惧的孩子，熄灭了寻友的篝火，在暗夜中发抖。自以为历经沧桑，其实刚刚蹒跚学步；自以为悟出了生存竞争的秘密，其实还远没有竞争的资格。<br/>\r\n使两个文明命悬一线的黑暗森林打击，不过是宇宙战场上一个微不足道的插曲。真正的星际战争没人见过，也不可能见到，因为战争的方式和武器已经远远超出人类的想象，目睹战场之日，即是灭亡之时。<br/>\r\n宇宙的田园时代已经远去，昙花一现的终极之美最终变成任何智慧体都无法做出的梦，变成游吟诗人缥缈的残歌；宇宙的物竞天择已到了最惨烈的时刻，在亿万光年暗无天日的战场上，深渊最底层的毁灭力量被唤醒，太空变成了死神广阔的披风。<br/>\r\n太阳系中的人们永远不会知道这一切，最后直面真相的，只有两双眼睛。', '39', '小说/科幻', null, 'book014.jpg', '2010-11');
INSERT INTO `booktable` VALUES ('6', '小小巴黎书店', '', '34.80', '塞纳河上，巴黎岸边，那艘停靠了21年的书船突然起航了。<br/>书船主人佩尔杜先生有口皆碑，他独自经营着这家叫“水上文学药房”的书店，自称“文学药剂师”。他与人为善，又个性执拗，会通过眼睛、耳朵和直觉，辨别出每一个灵魂所欠缺的东西，再把自己视为“解药”的书卖给对方。他以书为药， 相信唯有文学才能治愈人心。<br/>而他自己却深陷隐痛，围困其中。<br/>21年前，佩尔杜的挚爱曼侬忽然不告而别，留下一封信。他逃避着，不敢拆开它。<br/>21年后，佩尔杜意外得知曼侬的离去另有隐情，那不是一封分手信，而是一封求助信！<br/>他当即决定驾船南下，从巴黎前往曼侬的故乡普罗旺斯。<br/>与他意外同行的，是一位失去创作灵感的畅销书作家、一位追寻爱人多年的意大利厨师，还有两只博览群书的猫。<br/>', '6', '小说/外国', '', 'book006.jpg', '2001-02-05');
INSERT INTO `booktable` VALUES ('56', '那时，我们还不怕相爱', '', '36.80', '湖南卫视《天天向上》特邀嘉宾飞行官小北最新疗愈故事集！丁丁张、silver是水果味儿的、秦昊、张小厚、极光光，拍！手！推！荐！<br/>\r\n超过200,000,000人次阅读，疗愈60,000,000个小伙伴的超走心励志情感故事！', '34', '小说/青春', null, 'book007.jpg', '2015-4');
INSERT INTO `booktable` VALUES ('57', '乖，摸摸头', '', '36.00', '真实的故事自有万钧之力<br/>\r\n这本书讲述了12个真实的故事<br/>\r\n或许会让你看到那些你永远无法去体会的生活<br/>\r\n见识那些可能你永远都无法结交的人<br/>\r\n【乖，摸摸头】：“杂草敏”是一只南方姑娘，辞去了稳定的幼儿园老师工作，跑到济南某电视台，从剪片子做起。后来又离开了山东，蒲公英一样漂去了北京又漂回了南方，甚至还漂到了澳大利亚……<br/>\r\n【我有一碗酒，可以慰风尘】：这是一个关于越战老兵的故事。他将所有用命换来的补贴终身无偿捐献给希望工程、自费组建了第一支民间消防队，从不屑解释也不愿争辩的老兵，心里藏着一个关于铭记与奉献的故事。<br/>\r\n【普通朋友】：每个硬着骨头敢拼敢搏的人都有个柔软的理由。所有人都看到“屌丝男士”大鹏光辉灿烂的现在，而在此之前，他也曾经独自经历过漫长的黑夜。<br/>\r\n【不许哭】藏地往事，酒吧掌柜大冰、日语翻译妮可、民谣歌手赵雷的拉萨旧时光。', '35', '小说/青春', null, 'book008.jpg', '2014-9-1');
INSERT INTO `booktable` VALUES ('58', '为了你，我愿意热爱整个世界', '', '36.00', '我想去的那个地方，是你的心里。<br/>\r\n感动千万人的真爱告白 我们从未如此相信爱情。<br/>\r\n为了追到她，他一年内写下了137封情书，超过100万字。<br/>\r\n为了守护她，他创作了16部长篇小说，4000多万字，成就了网络文学的奇迹。<br/>\r\n唐家三少真实讲述了他与妻子十六年始终如一的爱情，也在书中完整披露了他从失业青年到明星作家的逆袭之路。<br/>\r\n磅礴而又深邃的感情，流畅而又通俗的文笔，自强不息奋力拼搏的情怀，为读者奉上了一部充满正能量的感人肺腑的励志爱情故事。', '36', '小说/青春', null, 'book009.jpg', '2016-6');
INSERT INTO `booktable` VALUES ('59', '从你的全世界路过', '让所有人心动的故事', '39.80', '6个月，畅销200万册，微博上最会写故事的人张嘉佳献给你的心动故事。<br/>\r\n最初以“睡前故事”系列的名义在网上疯狂流传，几天内达到1,500,000 次转发，超4亿次阅读，引来电影投资方的巨资抢购，转瞬便签下其中5个故事的电影版权。每1分钟，都有人在张嘉佳的故事里看到自己。<br/>\r\n读过睡前故事的人会知道，这是一本纷杂凌乱的书。像朋友在深夜跟你在叙述，叙述他走过的千山万水。那么多篇章，有温暖的，有明亮的，有落单的，有疯狂的，有无聊的，有胡说八道的。当你辗转失眠时，当你需要安慰时，当你等待列车时，当你赖床慵懒时，当你饭后困顿时，应该都能找到一章合', '38', '小说/青春', null, 'book010.jpg', '2013-11-1');
INSERT INTO `booktable` VALUES ('60', '谁的青春不迷茫', '一个奋斗小青年的逆袭人生', '35.00', '你觉得孤独就对了，那是让你认识自己的机会。你觉得不被理解就对了，那是让你认清朋友的机会。你觉得黑暗就对了，那是你发现光芒的机会。你觉得无助就对了，那样你才能知道谁是你的贵人。你觉得迷茫就对了，谁的青春不迷茫。<br/>\r\n“我一直在想如果自己遇见五年或者十年前的自己，我会对自己说些什么，问哪些问题。这于我一直是个疑惑。十年前，从我记录这本书的首个字开始，也许就在为这一天的对话做准备。”——刘同', '37', '小说/青春', null, 'book011.jpg', '2012-12');
INSERT INTO `booktable` VALUES ('61', '三体', '地球往事', '23.00', '文化大革命如火如荼进行的同时。军方探寻外星文明的绝秘计划“红岸工程”取得了突破性进展。但在按下发射键的那一刻，历经劫难的叶文洁没有意识到，她彻底改变了人类的命运。地球文明向宇宙发出的第一声啼鸣，以太阳为中心，以光速向宇宙深处飞驰……<br/>\r\n四光年外，“三体文明”正苦苦挣扎——三颗无规则运行的太阳主导下的百余次毁灭与重生逼迫他们逃离母星。而恰在此时。他们接收到了地球发来的信息。在运用超技术锁死地球人的基础科学之后。三体人庞大的宇宙舰队开始向地球进发……<br/>\r\n人类的末日悄然来临。', '39', '小说/科幻', null, 'book012.jpg', '2008-1');
INSERT INTO `booktable` VALUES ('62', '三体II', '黑暗森林', '32.00', '三体人在利用魔法般的科技锁死了地球人的科学之后，庞大的宇宙舰队杀气腾腾地直扑太阳系，意欲清除地球文明。<br/>\r\n面对前所未有的危局，经历过无数磨难的地球人组建起同样庞大的太空舰队，同时，利用三体人思维透明的致命缺陷，制订了神秘莫测的“面壁计划”，精选出四位“面壁者”。秘密展开对三体人的反击。<br/>\r\n三体人自身虽然无法识破人类的诡谲计谋，却依靠由地球人中的背叛者挑选出的“破壁人”，与“面壁者”展开智慧博弈……<br/>\r\n“面壁计划”究竟能否成功？地球人究竟能否在这场你死我活的文明生存竞争中战而胜之？神秘的<br/>\r\n“黑暗森林”究竟意味着什么？', '39', '小说/科幻', null, 'book013.jpg', '2008-5');
INSERT INTO `booktable` VALUES ('63', '微纪元', '', '26.80', '本作品为刘慈欣近年发表的短篇作品的合集，包括太原诅咒、微纪元、时间移民、微观尽头、坍缩、天使时代、诗云、思想者、赡养人类、纤维、西洋、信使、圆圆的肥皂泡共十三篇。', '39', '小说/科幻', null, 'book015.jpg', '2010-4');
INSERT INTO `booktable` VALUES ('64', '2018', '', '49.80', '公元2018年，网络信息技术飞速发展，在现实国家中衍生出虚拟国家，现实国家中的人类在虚拟国家拥有着另一种截然不同的身份。虚拟国家人口数量已达到20亿，虚拟的国际社会不但架构起完备的国际社群，更向联合国申请获得与现实国家同等的地位。与此同现时现实社会中，不计其数的人类将面临着知识结构过时的悲惨境况。无论是加入虚拟国家还是继续存留于现实国家，主人公都必然无所适从，于是他窃取公款对自己进行了基因改造，此后，他将拥有长达三百年的漫长人生去应对一切。', '39', '小说/科幻', null, 'book016.jpg', '2015-7');
INSERT INTO `booktable` VALUES ('65', '嫌疑人X的献身', '', '28.00', '百年一遇的数学天才石神，每天唯一的乐趣，便是去固定的便当店买午餐，只为看一眼在便当店做事的邻居靖子。<br/>\r\n靖子与女儿相依为命，失手杀了前来纠缠的前夫。石神提出由他料理善后。石神设了一个匪夷所思的局，令警方始终只能在外围敲敲打打，根本无法与案子沾边。石神究竟使用了什么手法？', '2', '小说/悬疑', null, 'book017.jpg', '2008-9');
INSERT INTO `booktable` VALUES ('66', '假面饭店', '', '39.80', '在高档酒店“东京柯尔特西亚”发生了连续预告杀人事件。负责追踪事件的警视厅刑警新田浩介扮成服务生混入酒店。与前台小姐山岸尚美一起探索连续杀人事件之谜。事件由东京都内发生的三件连续杀人事件现场遗留的数列暗号为开端。搜查总部通过对这三次事件的暗号进行分析，推断出第四次杀人将发生于“东京柯尔特西亚酒店”。为了阻止第四次事件的发生，数名搜查员被安置在酒店内，以服务生的身份一边勤奋于酒店工作一边进行搜查行动。其中一名搜查员便是搜查一科的刑警新田浩介。<br/>\r\n而由酒店所任命担当新田浩介助手一职的优秀前台接待山岸尚美，在警方潜入调查行动的初期曾与新田浩介不和。但是随着时间的经过，他们时而相互交换着服务生与搜查员的待事出发点，时因而意见冲突所对立，经历着酒店日常中所发生的悲喜交加的一件件小故事。慢慢地两人之间形成了深厚的信赖关系。终于，由搜查本部所处心积虑安排下的特别行动之日即将来到。<br/>\r\n在本故事之中特别注重了新田浩介身为一个搜查员，同时也身份一个凡人的成长过程。以及山岸尚美从顽固强硬到慢慢去主动理解新田的态度变化等非常微妙的心理变化描写。', '2', '小说/悬疑', null, 'book018.jpg', '2012-12');
INSERT INTO `booktable` VALUES ('67', '湖畔', '', '32.00', '为了孩子的入学考试，<br/>\r\n四个家庭来到湖边别墅进行考前集训。<br/>\r\n丈夫的情人尾随而至，却在深夜遇害。<br/>\r\n妻子坦白：“是我杀的。”<br/>\r\n丈夫打算报警处理，<br/>\r\n但邻居们竭力帮助妻子隐瞒罪行，<br/>\r\n在众人的劝说下丈夫终于加入了毁尸灭迹的行列，<br/>\r\n然而冷静下来他才意识到这一切非常不合情理。<br/>\r\n当真相被揭开之时，他却已无法挣脱', '2', '小说/悬疑', null, 'book019.jpg', '2016-1');
INSERT INTO `booktable` VALUES ('68', '虚无的十字架', '', '39.80', '所谓的“罪”与“罚”究竟本质为何？<br/>\r\n是让犯人听到自己的死刑宣判而感到解脱？<br/>\r\n还是，让他重返自由社会、但用尽一生赎罪？<br/>\r\n爱女被杀害的道正与小夜子夫妻在凶手被宣判死刑后，感到人生失去目标，即使凶手伏法，女儿也无法再复活的痛苦，终使道正与小夜子分手。某日，道正接到刑警致电，带来令人震惊的消息──小夜子被杀了。虽然不久后凶手自首，但道正却在小夜子的遗物中有所发现，她的死因似乎并不单纯……<br/>\r\n如果你的家人被残酷地夺走，你希望该如何处置犯人？<br/>\r\n如果犯人并未把死刑视为惩罚，至死仍未反省，死刑又有何作用？', '2', '小说/悬疑', null, 'book020.jpg', '2015-6');
INSERT INTO `booktable` VALUES ('69', '拉普拉斯的魔女', '', '39.80', '两处温泉地，相继发生硫化氢中毒事件，虽然在教授清江调查后被判定为“不可能人为”，而以意外结案。然而种种疑点和现场出现的神秘少女，令前警察武尾、地球化学教授清江和负责调查事件的中冈始终无法释怀……<br/>\r\n“这个世界的未来，到底怎么样?”<br/>\r\n少女深深地叹了一口气，摇着头说 :“我跟你说，还是不知道比较幸福。”', '2', '小说/悬疑', null, 'book021.jpg', '2017-1-1');
INSERT INTO `booktable` VALUES ('70', '生命里最美好的春天', '', '36.00', '春天真的来了，弟弟的手和我的手紧紧握在了一起，<br/>\r\n弟弟和我永远地定格成了两个为自由而战的孩子。<br/>\r\n与六千万死难者相比，我们是如此幸运。<br/>\r\n1943年的法国，笼罩在二战的阴云下，政府成为纳粹的傀儡，警察变身为打手。犹太少年雷蒙变成了让诺，他失去了家庭、亲人，和唯一的弟弟投入了抵抗运动，成为众多为自由而战的孩子的一员。一切就像儿童游戏一样开始了，可惜的是，玩这场游戏的少年们还没来得及长大成人，便为自由献出了生命。', '40', '小说/外国', null, 'book022.jpg', '2016-1-1');
INSERT INTO `booktable` VALUES ('71', '那些我们没谈过的事', '', '30.00', '婚礼举行前夕，朱莉亚接到了父亲助理打来的电话。不出她的所料，他的父亲安东尼•沃尔什，成功的商人，失败的父亲，无法前来参加她的婚礼了。但这次，朱莉亚不得不承认，安东尼有一个无懈可击的理由：他去世了。<br/>\r\n尽管父女两人矛盾重重，但朱莉亚还是决定送父亲最后一程。他的父亲总是有办法突然失踪， 然后把她的生活搅得一团乱。<br/>\r\n葬礼第二天，朱丽亚发现父亲为她预留了一个惊喜。一次奇特的旅行……最终，我们能够说出多年埋藏在心间的那些话。', '40', '小说/外国', null, 'book023.jpg', '2012-11');
INSERT INTO `booktable` VALUES ('72', '假如这是真的', '', '22.00', '旧金山一家医院的住院实习医生劳伦出车祸后成了植物人。有一天，她出现在年轻建筑师阿瑟的浴室壁橱里，阿瑟成了唯一能见到她形象，听到她说话的人。两人不久产生了恋情。为了阻止院方对劳伦实施安乐死，阿瑟不顾一切滴从医院里盗走了劳伦的躯体。几经周折，劳伦终于苏醒，却不认识阿瑟了。', '40', '小说/外国', null, 'book024.jpg', '2011-12');
INSERT INTO `booktable` VALUES ('73', '偷影子的人', '', '29.80', '一个老是受班上同学欺负的瘦弱小男孩，因为拥有一种特殊能力而强大：他能“偷别人的影子”，因而能看见他人心事，听见人们心中不愿意说出口的秘密。他开始成为需要帮助者的心灵伙伴，为每个偷来的影子找到点亮生命的小小光芒。<br/>\r\n某年灿烂的夏天，他在海边邂逅了一位又聋又哑的女孩。他该如何用自己的能力帮助她？他将如何信守与她共许的承诺？<br/>\r\n一段缠绵多年的爱恋，一段进行中的不完美爱情，一部令整个法国为之动容的温情疗愈小说。', '40', '小说/外国', null, 'book025.jpg', '2012-6-20');
INSERT INTO `booktable` VALUES ('74', '自由的孩子', '', '32.00', '这是一曲青春的挽歌，关乎友情、关乎爱情，烽火硝烟中致青春无悔！<br/>\r\n1943年的法国笼罩在二战的阴云下。那年的我18岁，我乘上 12路电车，当我下车后，我从雷蒙变成了亚诺。战争拆散了我的家庭，却让我结识了一群来自天南地北的战友，还有一头红发的达米拉。我以为这就是恣意飞扬的青春：一起炸铁路，一起搞暗杀，一起为女孩争风吃醋<br/>\r\n纳粹的魔爪已然张开，盛大的游戏终将落幕。等待这些年轻战士的将是怎样的命运？是逃出生天还是命丧集中营？死亡列车上，渐行渐远的不止是故土，还有那份青涩的英雄梦，那段即逝的爱情', '40', '小说/外国', null, 'book026.jpg', '2013-7');
INSERT INTO `booktable` VALUES ('75', '红楼梦', '', '59.70', '《红楼梦》是一部百科全书式的长篇小说。以宝黛爱情悲剧为主线，以四大家族的荣辱兴衰为背景，描绘出18世纪中国封建社会的方方面面，以及封建专制下新兴资本主义民主思想的萌动。结构宏大、情节委婉、细节精致，人物形象栩栩如生，声口毕现，堪称中国古代小说中的经 典。<br/>\r\n由红楼梦研究所校注、人民文学出版社出版的《红楼梦》以庚辰（1760）本《脂砚斋重评石头记》为底本，以甲戌（1754）本、已卯（1759）本、蒙古王府本、戚蓼生序本、舒元炜序本、郑振铎藏本、红楼梦稿本、列宁格勒藏本（俄藏本）、程甲本、程乙本等众多版本为参校本，是一个博采众长、非常适合大众阅读的本子；同时，对底本的重要修改，皆出校记，读者可因以了解《红楼梦》的不同版本状况。', '41', '文学艺术/古典', null, 'book027.jpg', '1996-12');
INSERT INTO `booktable` VALUES ('76', '三国演义', '', '39.50', '《三国演义》又名《三国志演义》、《三国志通俗演义》，是我国小说史上最著名最杰出的长篇章回体历史小说。 《三国演义》的作者是元末明初人罗贯中，由毛纶，毛宗岗父子批改。在其成书前，“三国故事”已经历了数百年的历史发展过程。在唐代，三国故事已广为流传，连儿童都很熟悉。随着市民文艺的发展，宋代的“说话”艺人，已有专门说三国故事的，当时称为“说三分”。元代出现的《三国志平话》，实际上是从说书人使用的本子，虽较简略粗糙，但已初肯《三国演义》的规模。罗贯中在群众传说和民间艺人创作的基础上，又依据陈寿《三国志》及裴松之注中所征引的资料（还包括《世说新语》及注中的资料），经过巨大的创作劳动，写在了规模宏伟的巨著——《三国志通俗演义》全书24卷，240回。后来经过毛纶，毛宗岗父子批改，形成我们现在所见的《三国演义》120回版', '42', '文学艺术/古典', null, 'book028.jpg', '1998-5');
INSERT INTO `booktable` VALUES ('77', '水浒传', '', '50.60', '《水浒传》是我国第一部以农民起义为题材的长篇章回小说，是我国文学史上一座巍然屹立的丰碑，也是世界文学宝库中一颗光彩夺目的明珠。数百年来，它一直深受我国人民的喜爱，并被译为多种文字，成为我国流传最为广泛的古代长篇小说之一。<br/>\r\n《水浒传》具体而生动地描写了以宋江为首的农民起义发生、发展直至失财的整个过程，揭露了封建社会的黑暗、腐朽和统治阶级的种种罪恶，热情歌颂了起义英雄的反抗精神和正义行动，塑造了一大批梁山好汉的光辉形象，形象地揭示了封建社会“官逼民反”的客观真理和农民起义失败的内在原因。', '43', '文学艺术/古典', null, 'book029.jpg', '1997-1');
INSERT INTO `booktable` VALUES ('78', '西游记', '', '47.20', '《西游记》主要描写的是孙悟空保唐僧西天取经，历经九九八十一难的故事。唐僧取经是历史上一件真实的事。大约距今一千三百多年前，即唐太宗贞观元年（627），年仅25岁的青年和尚玄奘离开京城长安，只身到天竺（印度）游学。他从长安出发后，途经中亚、阿富汗、巴基斯坦，历尽艰难险阻，最后到达了印度。他在那里学习了两年多，并在一次大型佛教经学辩论会任主讲，受到了赞誉。贞观十九年（645）玄奘回到了长安，带回佛经657部。他这次西天取经，前后十九年，行程几万里，是一次传奇式的万里长征，轰动一时。后来玄奘口述西行见闻，由弟子辩机辑录成《大唐西域记》十二卷。但这部书主要讲述了路上所见各国的历史、地理及交通，没有什么故事。及到他的弟子慧立、彦琮撰写的《大唐大慈恩寺三藏法师传》，则为玄奘的经历增添了许多神话色彩，从此，唐僧取经的故事便开始在民间广为流传。', '44', '文学艺术/古典', null, 'book030.jpg', '2004-8');
INSERT INTO `booktable` VALUES ('79', '浮生六记', '', '32.00', '俞平伯：“《浮生六记》俨如一块纯美的水晶，只见明莹，不见衬露明莹的颜色；只见精微，不见制作精微的痕迹。然而我自信这种说法不至于是溢美。想读这书的，必有能辨别的罢。”<br/>\r\n林语堂：“我相信淳朴恬适自甘的生活——如芸所说“布衣菜饭，可乐终身”的生活，是宇宙间最美丽的东西。在我翻阅重读这本小册之时，每每不期然而然想到这安乐的问题——读了沈复的书每使我感到这安乐的奥妙，远超乎尘俗之压迫与人身之痛苦。”', '45', '文学艺术/古典', null, 'book031.jpg', '2015-9');
INSERT INTO `booktable` VALUES ('80', '活着', '', '20.00', '《活着(新版)》讲述了农村人福贵悲惨的人生遭遇。福贵本是个阔少爷，可他嗜赌如命，终于赌光了家业，一贫如洗。他的父亲被他活活气死，母亲则在穷困中患了重病，福贵前去求药，却在途中被国民党抓去当壮丁。经过几番波折回到家里，才知道母亲早已去世，妻子家珍含辛茹苦地养大两个儿女。此后更加悲惨的命运一次又一次降临到福贵身上，他的妻子、儿女和孙子相继死去，最后只剩福贵和一头老牛相依为命，但老人依旧活着，仿佛比往日更加洒脱与坚强。<br/>\r\n《活着(新版)》荣获意大利格林扎纳•卡佛文学奖最高奖项（1998年）、台湾《中国时报》10本好书奖（1994年）、香港“博益”15本好书奖（1994年）、第三届世界华文“冰心文学奖”（2002年），入选香港《亚洲周刊》评选的“20世纪中文小说百年百强”、中国百位批评家和文学编辑评选的“20世纪90年代最有影响的10部作品”。', '46', '文学艺术/现当代', null, 'book032.jpg', '2012-8');
INSERT INTO `booktable` VALUES ('81', '许三观卖血记', '', '16.80', '《许三观卖血记》是余华1995年创作的一部长篇小说，它以博大的温情描绘了磨难中的人生，以激烈的故事形式表达了人在面对厄运时求生的欲望。小说讲述了许三观靠着卖血渡过了人生的一个个难关，战胜了命运强加给他的惊涛骇浪，而当他老了，知道自己的血再也没有人要时，精神却崩溃了。', '46', '文学艺术/现当代', null, 'book033.jpg', '1998-9');
INSERT INTO `booktable` VALUES ('82', '第七天', '', '29.50', '“浓雾弥漫之时，我走出了出租屋，在空虚混沌的城市里孑孓而行。我要去的地方名叫殡仪馆，这是它现在的名字，它过去的名字叫火葬场。我得到一个通知，让我早晨九点之前赶到殡仪馆，我的火化时间预约在九点半。”<br/>\r\n这是余华最新长篇小说《第七天》的开篇，给读者留下了足够大的悬念，一个走向殡仪馆、将被火化的人，在死亡之后还能留给读者什么呢？这次余华用荒诞的笔触和意象讲述了一个比《活着》更绝望、比《兄弟》更荒诞的故事，让读者体会到一种寒冬腊月被囚禁于积年冰川里的寒冷，一种剧烈拉锯式切肤的疼痛和虐心，一种茫茫荒野身心俱疲后无着无落的绝望。', '46', '文学艺术/现当代', null, 'book034.jpg', '2019-6');
INSERT INTO `booktable` VALUES ('83', '狂人日记', '', '39.80', '《狂人日记》是鲁迅1918年发表的第一篇白话短篇小说，当时正值“五四”运动的前夜。由于辛亥革命的关途而废，特别是帝国主义侵略的加剧，使社会各种矛盾更加复杂尖锐。鲁迅以他锐敏的思想和犀利的笔触，对封建制度及其上层建筑表现了彻底的反抗。<br/>\r\n《狂人日记》是中国现代文学史上第一篇真正的现代白话小说。令人惊异的是，这部中国现代小说史上具有开山意义的作品，已经显示出极其成熟的特色，使后来的许多研究者为之倾倒，究其原因，除了鲁迅深厚的文学素养外，我们也无法不叹服于先生的天才。', '5', '文学艺术/现当代', null, 'book035.jpg', '2006-3');
INSERT INTO `booktable` VALUES ('84', '朝花夕拾', '', '20.00', '本书收入鲁迅先生1926年2月至11月间创作的散文10篇和小引1篇及后记1篇。《朝花夕拾》是“从记忆中抄出来”的散文，“以青少年时代生活经历为线索，真实而动人地抒写了从农村到城镇、从家庭到社会、从国内到国外的一组生活”。《朝花夕拾》艺术特色鲜明－－爱憎分明；叙述与议论、回忆与感想、抒情与讽刺有机结合；流畅自然，亲切平易，“有真意，去粉饰，少做作，勿卖弄”。', '5', '文学艺术/现当代', null, 'book036.jpg', '2001-10');
INSERT INTO `booktable` VALUES ('85', '老人与海', '', '8.20', '本书讲述了一个渔夫的故事。古巴老渔夫圣地亚哥在连续八十四天没捕到鱼的情况下，终于独自钓上了一条大马林鱼，但这鱼实在大，把他的小船在海上拖了三天才筋疲力尽，被他杀死了绑在小船的一边。在归程中，他再遭到一条鲨鱼的袭击，最后回港时只剩鱼头鱼尾和一条脊骨。而在老圣地亚哥出海的日子里，他的忘年好友一直在海边忠诚地等待，满怀信心地迎接着他的归来。', '47', '文学艺术/外国', null, 'book037.jpg', '1999-10');
INSERT INTO `booktable` VALUES ('86', '流动的盛宴', '', '28.00', '1920年代上半叶，海明威以驻欧记者身份旅居巴黎，《流动的盛宴》这本书，记录的正是作者当日的这段生活。不过这本书的写作却是在将近四十年以后，换句话说，盛宴的“现场”早已消失，作者和读者都只是在记忆中追寻那段过往岁月，而无论是作者或是读者，这些记忆都已在时光的透镜里失焦、变形。所有有关巴黎的记忆都杂糅成一种对于巴黎的共同的历史记忆。在历史上不计其数的关于巴黎的虚构或非虚构作品中，《流动的盛宴》是最著名的作品之一，其扉页上的题献——“假如你有幸年轻时在巴黎生活过，那么你此后一生中不论去到哪里她都与你同在，因为巴黎是一席流动的盛宴”，已经成为巴黎的“文化名片”，被广为传诵。在这部半纪实半虚构的作品中，二十年代巴黎文人圈的风俗画卷徐徐展开，那些当时在世界文坛、艺坛上呼风唤雨的人物都被寥寥数笔勾勒出生动的素描像，与这座城市、这部书永远融为一体。', '47', '文学艺术/外国', null, 'book038.jpg', '2009-1');
INSERT INTO `booktable` VALUES ('87', '乞力马扎罗的雪', '', '28.00', '本书从海明威的多本短篇小说合集中选取了十个最为人称道的短篇佳作，充分展示了作家一贯简洁洗练的文风。这十个短篇涉及了海明威毕生关注的拳击、狩猎、战争等主题，浓缩了作家对于恐惧下的众生相及生命的虚无的探讨。', '47', '文学艺术/外国', null, 'book039.jpg', '2006-8');
INSERT INTO `booktable` VALUES ('88', '永别了，武器', '', '18.00', '美国青年弗瑞德里克•亨利在第一次世界大战后期志愿参加红十字会驾驶救护车，在意大利北部战线抢救伤员。在一次执行任务时，亨利被炮弹击中受伤，在米兰医院养伤期间得到了英国籍护士凯瑟琳的悉心护理，两人陷入了热恋。亨利伤愈后重返前线，随意大利部队撤退时目睹战争的种种残酷景象，毅然脱离部队，和凯瑟琳会合后逃往瑞士。结果凯瑟琳在难产中死去。<br/>\r\n海明威根据自己的参战经历，以战争与爱情为主线，吟唱了一曲哀婉动人的悲歌，曾多次被搬上银幕，堪称现代文学的经典名篇。', '47', '文学艺术/外国', null, 'book040.jpg', '2006-8');
INSERT INTO `booktable` VALUES ('89', '白象似的群山', '', '30.00', '精选101篇世界短篇小说传世经典<br/>\r\n汇聚70位顶尖文学巨匠<br/>\r\n收录15国文学瑰宝<br/>\r\n凝成8册世界小说经典丛书<br/>\r\n一生必读的世界名家短篇小说经典', '47', '文学艺术/外国', null, 'book041.jpg', '2015-8-1');
INSERT INTO `booktable` VALUES ('90', '资治通鉴', '', '19.90', '《资治通鉴》是中国历史上规模最大、成就最高的编年体通史。全书上起周威烈王二十三年(前403)，下至五代后周显德六年(959)，共跨越1362年的历史。内容以政治、军事和民族关系为主，兼及经济、文化、学术思想和对历史人物的评价。编者运用敏锐深刻的观察力和匠心独具的艺术手法，不仅形象生动地展现了波澜壮阔的社会生活画面，还成功地塑造了大量栩栩如生的历史人物。', '48', '人文社科/历史', null, 'book042.jpg', '2006-7');
INSERT INTO `booktable` VALUES ('91', '史记', '', '19.90', '《史记》是我国纪传体通史的开山之作，被鲁迅先生称为“史家之绝唱，无韵之《离骚》”。全书包括本纪、表、书、世家和列传，共一百三十篇，记载了从黄帝到汉武帝年间长达3000年的政治、经济和文化的历史，全面而深刻地反映了我国古代的社会面貌。<br/>\r\n《史记》原名《太史公书》，是我国第一部纪传体通史。全书130篇，包括12本纪、8书、10表、30世家、70列传五个部分，共52万字，记载了从黄帝到汉武帝年间长达3000年的政治、经济和文化的历史，全面而深刻地反映了我国古代的社会面貌。《史记》具有强烈的批判精神和鲜明的人民性。它对封建统治阶级的黑暗腐朽作了深刻大胆的揭露。<br/>\r\n《史记》写完后，并没有立即公诸于世，而是在司马迁死后多年，由他的外孙杨恽把这部书拿出来让人传抄。此后，《史记》就成为后世史家修史时的必读之作，也使得司马迁的大名弘扬干后世。', '49', '人文社科/历史', null, 'book043.jpg', '2006-7');
INSERT INTO `booktable` VALUES ('92', '重说中国近代史', '', '32.00', '《本书源自人大的一门热门选修课，即张鸣老师开设的政治史公开课，它靠同学们口碑相传而走红校园，最终使更多的人对这段看起来枯燥无比的历史重新认真审视起来。<br/>\r\n这门课为什么如此受欢迎？原来在张鸣老师的还原下，中国近代史变得如此复杂、精彩，又是如此的颠覆，它与我们记忆里的古板印象迥然不同，绝非教科书上的忠奸两列、黑白分明。当诸多人物与史实呈现在我们面前时，难以用一句简单的是非作判定，在正视一段被扭曲的中国近代史的同时，我们也能发现国人今日问题的精神根源。<br/>\r\n基于此，我们将这门课的讲课稿集结成书，希望与更多的人分享这段充满矛盾与悖论的真实历史：中国与西方、清廷与民间、满族与汉族士人、洋教与本土信仰、枪杆与笔杆、造反和维新、科举与革命……是的，那段历史，除去屈辱与仇恨，还有着太多的内容值得重新去回望和反思。<br/>\r\n正如张鸣教授所说：如果我们不知道或者完全无视这个过程，就很难在历史长河中找到自己的位置，很难安放好自己的位置，在世界格局中也定位不好自己的位置。诚如斯言。', '50', '人文社科/历史', null, 'book044.jpg', ' 2012-2');
INSERT INTO `booktable` VALUES ('93', '马伯庸笑翻中国简史', '', '29.90', '这是一部最精短、更生动的中国通史。“文字鬼才”马伯庸上起战国秦汉，下至晚清民国，以寥寥数笔，勾勒出中国历史的线索；打破以政治大事为纬、以帝王更替为经的写史手法，用最新颖的主题“王朝的德性”重新串联历史；穿插以马式冷幽默的犀利评点，将枯燥单调的历史真实变得新鲜有趣。<br/>\r\n翻开《马伯庸笑翻中国简史》，战国、秦、汉、三国、隋、唐、宋、元等历史时期将轮番上阵，颠覆争霸、强盛的历史形象，展现它们不为人知的一面；新朝、前赵、前燕、冉魏、刘宋、后周等并不起眼的政权也一个不落、各具特色地接连登场，在魏晋南北朝、五胡十六国、五代十国等混乱时代演出一场场鲜活、生动的荒诞剧目。<br/>\r\n读完本书，你将在最短的时间内，轻松架构起中国历史的轮廓；在轻松、欢乐的行文中，领悟历史背后的荒唐与无奈。', '51', '人文社科/历史', null, 'book045.jpg', ' 2015-2-1');
INSERT INTO `booktable` VALUES ('94', '李鸿章时代', '', '46.00', '《李鸿章时代》一书从1870年在欧亚大陆的两端各发生的一件大事讲起。在大清国，李鸿章出任直隶总督；在欧洲则爆发了普法战争。1895年，太平洋的两岸同样各发生一件大事。在西岸，日本在甲午战争中击败中国，李鸿章被撤销直隶总督一职；在东岸，美国的GDP总值第一次超越大清国，跃居世界第一位。 从1870年到1895年的26年，是李鸿章主政北洋，“应对三千年未有之变局”的26年，也是全球权力格局巨变的26年，更是晚清错失战略机遇、加速进入下行曲线的26年。其间之内政外交，无论是成是败，为是为非，李鸿章都扮演了核心角色。因而，这26年也就成了一个不折不扣的李鸿章时代。 如何认识这样一个时代？正如托克维尔在《旧制度与大革命》中给出的答案：“谁要是只研究和考察法国，谁就永远也无法理解法国大革命。”同样的道理也适用于中国。本书广泛搜集中、日、法等多国史料，以19世纪后期的国际格局巨变为背景，以日本入侵中国台湾、中法战争、中日甲午战争三大事件为波峰，穿插李鸿章主持的内部变革，强调从大历史与大战略的双重视角，全新解读这段似乎国人已经非常熟悉，其实依然非常陌生的往事。', '52', '人文社科/历史', null, 'book046.jpg', ' 2013-5');
INSERT INTO `booktable` VALUES ('95', '我们为什么会发胖？', '以及到底该怎么减肥', '39.00', '为什么有的人吃再多也不胖，有的人喝凉水也发胖？为什么严格、认真节食的人依旧会遭遇反弹的痛苦？<br/>\r\n美国最佳医学科普作者盖里•陶比斯决心为公众找到答案。他花费整整10年，全面调查了一百多年来减肥科学的文献和最前沿的实验，终于发现了肥胖的惊人真相和科学减肥的唯一做法：肥胖者比普通人需要更多的营养来滋养身体，此时盲目少吃，可能会给身体带来不可逆的伤害。要想一次性彻底减肥，必须先优化营养结构，不在于吃多少，而在于吃什么、怎么吃。<br/>\r\n本书一出，立刻震惊国际医学界，美国数千个减肥中心和研究所更新减肥方案，全球数千万肥胖者因此重返健康。最终，美国政府决定重新制定健康减肥新标准，陶比斯也因此三度获得美国国家科学作者奖。本书被誉为21世纪突破减肥瓶颈的大发现。<br/>\r\n翻开本书，揭开肥胖的真实原因，帮你找到减肥的唯一答案，科学减肥绝不失败！<br/>\r\n史上最有智慧的减肥书。——《纽约时报》<br/>\r\n陶比斯是科普作者们的科普作者，他从不愚弄读者。——《科学美国人》', '53', '品质生活/健康', null, 'book047.jpg', ' 2015-5');
INSERT INTO `booktable` VALUES ('96', '身体知道答案', '', '36.00', '《身体知道答案》作者从一名心理学者的角度，结合大量生动的案例，分析了身体与心灵、意识与潜意识的关系，指出了我们的心是如何构建了自己的世界的。我们身体的很多反应，其实都是有着深层的心理原因的，因而作者提倡回归身体，将身体与心灵链接起来，“成为真实的你自己”，做到这一点后，你将发现，生命真的可以是自由的。', '54', '品质生活/健康', null, 'book048.jpg', ' 2013-1');
INSERT INTO `booktable` VALUES ('97', '硬派健身', '你的第一本健身书', '48.00', '十年前，我开始了自己的健身训练。然而，我却走了不少弯路，听信了不少错误的传言。<br/>\r\n十年间，我发现这世界有太多的人云亦云，太多的误人子弟，太多的急功近利，太多的寡廉鲜耻。<br/>\r\n十年后，此时、此地，我愿《硬派健身》，成为你的第一本健身书。', '55', '品质生活/健康', null, 'book049.jpg', ' 2015-6-1');
INSERT INTO `booktable` VALUES ('98', '运动饮食1︰9', '', '32.00', '真正有效的减肥方法，并不是为了促进脂肪燃烧而进行的运动，也不是严格的限制饮食，而是找到造成自己现在身体状况的原因，根据原因采取的对应的方法。归根结底，是如何面对饮食的问题。“不改变饮食习惯，只想通过运动甚至其他捷径来减肥”这种想法本身就是有问题的。<br/>\r\n本书将 消除大家对减肥和运动之间关系的错误认识，对如何切实地取得减肥成果，进行通俗易懂的讲解。<br/>\r\n本书所提出的方法并不是谁也做不到的难题。只要找出自己为何难以取得减肥成果的原因，就能够知道自己应该做什么、怎么做，最终减肥成果，获得理想的身材和健康的体质。目前正风靡全日本的饮食减肥法，教你吃对食物，躺着也能瘦。', '61', '品质生活/健康', null, 'book050.jpg', ' 2015-6');
INSERT INTO `booktable` VALUES ('99', '吃货的50种情绪减肥法', '轻松幸福的瘦身之旅', '30.00', '如果你经常无意识地用吃来减轻压力、改善情绪，导致无法控制体重<br/>\r\n正在减肥，但运动或节食的压力让你越吃越多<br/>\r\n减肥成功后需要保持身材，但因为控制不了食欲而反弹<br/>\r\n想戒掉某种不健康的习惯（抽烟、喝酒……），但不知用什么方式替代<br/>\r\n请把本书放在手边，随时使用美国著名体重管理专家、心理学家阿尔伯斯博士的情绪减肥法。', '56', '品质生活/健康', null, 'book051.jpg', ' 2014-1');
INSERT INTO `booktable` VALUES ('100', '把每一天，当作梦想的练习', '', '29.80', '本书是职场新人和职场中人最需要的生存指南，平衡职场思维、正确的工作态度、智慧而不是辛苦地工作、知行合一、与老板做朋友、学会沟通、职场人士该做的十六件事……每一篇文章都饱含作者丰富的职场经历经验，无论是职场新手还是老江湖，都可以从中找到共鸣以及醍醐灌顶的顿悟，在职业旅途中离梦想更进一步。<br/>\r\n这是一部职场成功人士的奋斗史，也是一部幸福人生的指导书。有故事，有知识，有技术，有方法。这本书为即将踏上社会的学生打开了一扇了解职场的窗，为职场新人指明了前行的路，让已经在路上的人看到圆融、平衡、幸福的可能。活得精彩，让生命绽放，你也可以做到！', '57', '成功励志/职场', null, 'book052.jpg', ' 2014-1');
INSERT INTO `booktable` VALUES ('101', '办公室里的浮沉之道', '', '28.00', '《办公室里的浮沉之道》利用中国传统谋略经典《鬼谷子》的理论资源，结合当代职场实际情况，为读者提供了一整套升职、加薪路线图。初入职场者手持《办公室里的浮沉之道》，能够很快适应职场环境，为未来发展奠定有力的基础；中层管理者手持《办公室里的浮沉之道》，能够摸透职场人心，成为杰出的领导者。《办公室里的浮沉之道》通过一个个扣人心弦的职场故事，展现了职场生活的方方面面；通过犀利深刻的解析，为你揭开办公室浮沉背后的秘密。', '58', '成功励志/职场', null, 'book053.jpg', ' 2013-1');
INSERT INTO `booktable` VALUES ('102', '华为狼道', '', '39.00', '《华为狼道》内容简介：华为技术有限公司成立于1987年，做代理起家，2009年跻身全球第二大信息与通信设备商，2010年成为世界500强企业，2011年入选首批“国家技术创新示范企业”、位居“中国民营企业500强”第一。20多年时间，华为逐步发展成一家业务遍及140多个国家和地区的全球化公司，以优异的成绩单演绎了什么叫做“中国企业的标杆”。<br/>\r\n华为做大做强到底凭什么？是其领军人任正非有背景？还是华为有秘密武器？《华为狼道》通过对华为的企业文化、市场扩张、技术研发、人才战略、危机管理、组织架构，以及接班人风波、不上市谜局等的剖析，揭开了华为迅速壮大的终极哲学——狼道。', '62', '成功励志/职场', null, 'book054.jpg', ' 2012-7');
INSERT INTO `booktable` VALUES ('103', '你的效率是整理出来的', '', '29.80', '生活中的种种不畅往往是因为缺乏合理的安排和及时调整。物品无序摆放，它就和你捉迷藏了；金钱随便存放，它就会在不经意中流失；人生缺乏规划，就很难有所成就；心情乱七八糟，心里就会难受……因为很多时候我们会处于失衡状态，所以，人生需要你不断去矫正才行。物品摆放好你才能随手拿到，文件收纳好你才能信手拈来，环境整理好你才能神清气爽，财富打理好你才会财源滚滚，工作安排好你才能提高效率……而这些只能通过“整理”才能做到。通过整理，我们能为自己及时建立合理的生活秩序和生活平衡，提高人生的效率。<br/>\r\n如果说物品整理是一般的整理术的话，《高效能人士的超级整理术》就是一般“整理术”的升级版，在教会读者物品整理的同时，将整理的智慧延伸到生活中的方方面面，包括物品收纳、文件处理，优化生活环境、财富打理、工作安排和情绪调整等生活的各个方面。<br/>\r\n超级整理术是一种不依靠卓越的才能和坚强的意志力，不依靠高学历，不依靠超强的记忆力，只要按设定的规则去做，你的人生效率就会得到大幅度提升。', '59', '成功励志/职场', null, 'book055.jpg', ' 2013-11-1');
INSERT INTO `booktable` VALUES ('104', '职场菜鸟升职记', '', '25.00', '《职场菜鸟升职记》以自传体形式讲述了一名计算机专业的大学本科毕业生张午阳，在短短的一年时间内，由职场菜鸟晋升为项目经理的故事。《职场菜鸟升职记》精心揭示了一只职场菜鸟的进化秘密：从懵懵懂懂地进入公司，到游刃有余的老员工；从参加新人培训的“呆头鹅”，到带领团队克服困难的“领头羊”；从头脑空白地接受第一项工作任务，到驾轻就熟地成为公司不可或缺的人才……，张午阳的每一个成长细节，都值得我们认真分析，学习。晋升和成长，属于张午阳这样细心和有准备的人。', '60', '成功励志/职场', null, 'book056.jpg', ' 2009-1');

