-- ----------------------------
-- Table structure for writertable
-- ----------------------------
DROP TABLE IF EXISTS `writertable`;
CREATE TABLE `writertable` (
  `writer_id` int(11) NOT NULL AUTO_INCREMENT,
  `writer_name` varchar(30) NOT NULL,
  `writer_content` text,
  PRIMARY KEY (`writer_id`)
) ENGINE=InnoDB AUTO_INCREMENT=63 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of writertable
-- ----------------------------
INSERT INTO `writertable` VALUES ('1', '村上春树', '村上春树，日本后现代主义作家，1949年1月12日生于京都伏见区。毕业于早稻田大学第一文学部演剧科。');
INSERT INTO `writertable` VALUES ('2', '东野圭吾', '东野圭吾，日本推理小说作家。1958年2月4日出生于日本大阪。毕业于大阪府立大学电气工学专业，之后在汽车零件供应商担任生产技术工程师。');
INSERT INTO `writertable` VALUES ('3', '弗·司各特·菲茨杰拉德', '弗朗西斯·斯科特·基·菲茨杰拉德（Francis Scott Key Fitzgerald，1896年9月24日－1940年12月21日），20世纪美国作家、编剧。');
INSERT INTO `writertable` VALUES ('4', '余华', '余华，1960年4月3日生于浙江杭州，当代作家。中国作家协会第九届全国委员会委员。');
INSERT INTO `writertable` VALUES ('5', '鲁迅', '鲁迅（1881年9月25日－1936年10月19日），原名周樟寿，后改名周树人，字豫山，后改豫才，“鲁迅”是他1918年发表《狂人日记》时所用的笔名，也是他影响最为广泛的笔名，浙江绍兴人。著名文学家、思想家，五四新文化运动的重要参与者，中国现代文学的奠基人。毛泽东曾评价：“鲁迅的方向，就是中华民族新文化的方向。”');
INSERT INTO `writertable` VALUES ('6', ' 加·泽文', '毕业于哈佛大学英美文学系，已经出版了八部小说，作品被翻译成二十多种语言。14岁时，她写了一封关于“枪与玫瑰乐团”的信函投给当地报社，措辞激烈，意外获得该报的乐评人一职，迈出了成为作家的第一步。一直以来，她对书、书店以及爱书人的未来，充满见解。');
INSERT INTO `writertable` VALUES ('34', '飞行官小北', '作家，编剧，自由撰稿人，志愿守夜人，差一年零一个月就可以跻身九〇后的人<br/>\r\n曾出版文集《别闹，少年》<br/>\r\n人生最大荣誉：大三荣获理工科大学肄业证书<br/>\r\n人生最大遗憾：因为上大学而浪费掉两年时间<br/>\r\n人生最大爱好：搞懂人，人与人，以及所有人<br/>\r\n人生最大愿望：依然是也将一直是，一生年少');
INSERT INTO `writertable` VALUES ('35', '大冰', '大冰，1980年生人、作家、某卫视首席主持人、某高校导师、民谣歌手、老背包客、不敬业的酒吧掌柜、油画科班、手鼓艺人、业余皮匠、业余银匠、业余诗人、资深西藏拉漂、资深丽江混混、黄金左脸、禅宗临济弟子');
INSERT INTO `writertable` VALUES ('36', '唐家三少', '唐家三少 ：本名张威，生于北京，80后摩羯座。<br/>\r\n中国网络文学代表作家。2004年开始文学创作，代表作有《光之子》《斗罗大陆》《绝世唐门》《龙王传说》等16部长篇小说。<br/>\r\n2012—2015年，连续四次稳居中国网络文学作家富豪榜榜首。2015年以1.1亿元刷新纪录，成为首位年版税收入突破一亿元的作家。2014—2015年，两次入选福布斯中国名人榜，是名人榜中仅有的三位作家之一。');
INSERT INTO `writertable` VALUES ('37', '刘同', '我叫刘同。现在住在北京四环旁边一个叫沿海赛洛城的楼盘里。七年前也曾经幻想以写字为生。但无奈学识有限，北京太大，我写出来的那些字都不够成为我容身的砖瓦城墙。还好，我生性贫贱，嘴贫性格贱，从不抱怨自己的遭遇，所以投身传媒这一行，至今。<br/>\r\n以前我是城市旅人，为工作奔走城市间；后来他们叫我职场达人，为生活奔走于工作。<br/>\r\n现在，我和你一样。依然在路上。');
INSERT INTO `writertable` VALUES ('38', '张嘉佳', '张嘉佳，毕业于南京大学，出版小说《几乎成了英雄》《情人书》《刀见笑》。所著《小夫妻天天恶战》《姐姐的故事》等文章也反响巨大。曾任电影《刀见笑》编剧，获2011年金马奖最佳改编剧本提名。《从你的全世界路过》是一本短篇集，即“睡前故事”系列，发表后广为流传，总阅读量超过四亿，2013年引发风暴，成为奇迹。由书中故事改编的电影项目，现已确定拍摄的高达五部。');
INSERT INTO `writertable` VALUES ('39', '刘慈欣', '刘慈欣，祖籍河南，长于山西，中国科普作家协会会员，山西省作家协会会员，高级工程师。<br/>\r\n自1999年处女作《鲸歌》问世以来，刘慈欣已发表短篇科幻小说三十余篇、出版长篇科幻小说六部，并创下连续八年荣获中国科幻最高奖“银河奖”的纪录。他的长篇作品《三体》开创《科幻世界》月刊连载原创作品的先例，成为2006年度最受关注、最畅销的科幻小说。');
INSERT INTO `writertable` VALUES ('40', '[法]马克·李维', '马克•李维（Marc Levy）：全世界拥有最多读者的法国作家，作品热销全球49个国家，总 销量超过3500万册，连续15年蝉联“法国最畅销作家”，拥有让大导演斯皮尔伯格只看两页书稿，就重金购下电影版权的神奇魅力。已在中国出版畅销书《偷影子的人》《伊斯坦布尔假期》《如果一切重来》《比恐惧更强烈的情感》《第一日》《第一夜》《幸福的另一种含义》《那些我们没谈过的事》《在另一种生命里》等。');
INSERT INTO `writertable` VALUES ('41', '曹雪芹', '曹雪芹，清小说家。名霑，字梦阮，号雪芹、芹圃、芹溪。为满洲正白旗“包衣”人。自曾祖起，三代任江宁织造，其祖曹寅尤为康熙帝所信用。雍正初年，在统计阶级内部政治斗争牵连下，雪芹家受到重大打击，其父免职，产业被抄，遂随家迁居北京。他早年经历了一段封建大官僚地主家庭的繁华生活，后因家道衰落，趋于艰困。晚期居北京西郊，贫病而卒，年未及五十。性情高傲，嗜酒健谈。具有深厚的文化修养和卓越的艺术才能。他生活在我国已有资本主义生产萌芽的封建末世，在其后期又有机会接触到下层人民，因而对当时社会阶级斗争和思想斗争有较具体的感受，看到了统治阶级的腐朽凶残和内部的分崩离析。曾以十年时间，从事《石头记》（即《红楼梦》）的创作。书中通过一个贵族官僚大家庭的盛衰历史的描写，塑造了许多典型人物形象，对当时社会的黑暗腐败，进行了深刻的解剖和批判，并热情地歌颂了具有异端思想的男女青年，成为我国古典小说中伟大的现实主义作品。但其中也反映了作者为封建制度“补天”的幻想和找不到出路的悲观情绪。据称先后曾增删五次，但未成全书而卒；今流行本一百二十回，后四十回一般认为是高鹗所续。也能诗，又善画石，但作品流传绝少。');
INSERT INTO `writertable` VALUES ('42', '罗贯中', '罗贯中，汉族，名本，字贯中，号湖海散人。山西太原人，一说钱塘（现在浙江杭州）或庐陵（现在江西吉安）人。元末明初著名小说家、戏曲家，是中国章回小说的鼻祖。一生著作颇丰，主要作品有：剧本《赵太祖龙虎风云会》《忠正孝子连环谏》、《三平章死哭蜚虎子》；小说《隋唐两朝志传》《残唐五代史演义》《三遂平妖传》《粉妆楼》、和施耐庵合著的《水浒传》、代表作《三国演义》等。');
INSERT INTO `writertable` VALUES ('43', '施耐庵', '施耐庵，，原名彦端，字肇瑞，号子安，别号耐庵。今江苏兴化人。元末明初的文学家，本名彦端，汉族，祖籍是泰州海陵县或苏州吴县阊门（今江苏苏州）。博古通今，才气横溢，举凡群经诸子，词章诗歌，天文、地理、医卜、星象等，一切技术无不精通，35岁曾中进士，后弃官归里，闭门着述，与门下弟子罗贯中一起研究《三国演义》《三遂平妖传》的创作，搜集整理关于梁山泊、宋江等英雄人物的故事，最终写成“四大名著”之一的《水浒传》。施耐庵于元延祐元年（1314年）中秀才，泰定元年（1324年）中举人，至顺二年（1331年）登进士不久任浙江钱塘县尹。施耐庵故里江苏兴化新垛乡施家桥村有墓园、纪念馆，有《施氏家薄谱》存世。');
INSERT INTO `writertable` VALUES ('44', '吴承恩', '吴承恩，字汝忠，号射阳山人。汉族，淮安府山阳县（今江苏省淮安市淮安区）人。祖籍安徽桐城高甸（今枞阳县雨坛乡高甸），以祖先聚居枞阳高甸，故称高甸吴氏。父吴锐，字廷器，卖“彩缕文羯”，是“又好谭时政，竟有所不平，辄抚几愤惋，意气郁郁”之人。因随祖辈迁徙至淮安府（今江苏省淮安市淮安区）人。中国明代杰出的小说家，是四大名著之一《西游记》的作者。他生于一个由学官沦落为商人的家族，家境清贫。吴承恩自幼聪明过人，《淮安府志》载他“性敏而多慧，博极群书，为文下笔立成。”但他科考不利，至中年才补上“岁贡生”，后流寓南京，长期靠卖文补贴家用。晚年因家贫出任长兴县丞，由于看不惯官场的黑暗，不久愤而辞官，贫老以终。');
INSERT INTO `writertable` VALUES ('45', '沈复', '沈复，字三白，号梅逸。清乾隆二十八年（1763年）生于姑苏城南沧浪亭畔士族文人之家，十八岁娶舅女陈芸为妻。婚后夫妻俩举案齐眉、相爱甚笃，然命途多舛，常常事与愿违；幸而二人不落世俗，善苦中作乐，耳鬓厮磨二十三年，至芸积病身故，仍情深如旧。后，沈复离家漫游，著《浮生六记》六卷，记录过往生活中点滴趣味及漫游经历，因其以真言述真情，从不刻意造作，深为后世文人所推崇，流传至今，已成经典。');
INSERT INTO `writertable` VALUES ('46', '余华', '余华，1960年出生，1983年开始写作。至今已经出版长篇小说4部，中短篇小说集6部，随笔集4部。主要作品有《兄弟》《活着》《许三观卖血记》《在细雨中呼喊》等。其作品已被翻译成20多种语言在美国、英国、法国、德国、意大利、西班牙、荷兰、瑞典、挪威、希腊、俄罗斯、保加利亚、匈牙利、捷克、塞尔维亚、斯洛伐克、波兰、巴西、以色列、日本、韩国、越南、泰国和印度等国出版。曾获意大利格林扎纳·卡佛文学奖（1998年）、法国文学和艺术骑士勋章（2004年）、中华图书特殊贡献奖（2005年）、法国国际信使外国小说奖（2008年）等。');
INSERT INTO `writertable` VALUES ('47', '[美]海明威', '欧内斯特•米勒•海明威（英文：Ernest Miller Hemingway，1899年7月21日－1961年7月2日），美国记者、作家以及二十世纪最著名的小说家之一。海明威出生于美国伊利诺伊州芝加哥市郊区的奥克帕克，晚年在爱达荷州凯彻姆的家中自杀身亡。海明威的一生感情错综复杂，先后结过四次婚，是美国「迷失的一代」（Lost Generation）作家中的代表人物，作品中对人生，世界，社会都表现出了迷茫和彷徨。');
INSERT INTO `writertable` VALUES ('48', '司马光', '司马光（1019年11月17日－1086年），男，字君实，号迂叟，汉族，陕州夏县（今山西夏县）涑水乡人，世称涑水先生，北宋政治家、史学家、文学家。历仕宋仁宗、宋英宗、宋神宗、宋哲宗四朝，卒赠太师、温国公，谥文正，政治上对西夏、辽国采取割地忍让政策，并上《上哲宗乞还西夏六寨》。其人格堪称儒学教化下的典范，历来受人景仰。宋仁宗时中进士，英宗时进龙图阁直学士。宋神宗时，反对王安石施行变法，朝廷内外有许多人反对，司马光就是其中之一。王安石变法以后，司马光离开朝廷十五年，主持编纂了中国历史上第一部编年体通史《资治通鉴》。生平着作甚多，主要有史学巨着《资治通鉴》、《温国文正司马公文集》、《稽古录》、《涑水记闻》、《潜虚》等。');
INSERT INTO `writertable` VALUES ('49', '司马迁', '司马迁（前145年-不可考），字子长，夏阳(今陕西韩城南)人。西汉史学家、散文家。司马谈之子，任太史令，因替李陵败降之事辩解而受宫刑，后任中书令。发奋继续完成所著史籍，被后世尊称为史迁、太史公、历史之父。 　　司马迁早年受学于孔安国、董仲舒，漫游各地，了解风俗，采集传闻。初任郎中，奉使西南。元封三年（前108）任太史令，继承父业，著述历史。他以其“究天人之际，通古今之变，成一家之言”的史识创作了中国第一部纪传体通史《史记》（原名《太史公书》）。被公认为是中国史书的典范，该书记载了从上古传说中的黄帝时期，到汉武帝元狩元年，长达3000多年的历史，是“二十五史”之首，被鲁迅誉为“史家之绝唱，无韵之离骚”。');
INSERT INTO `writertable` VALUES ('50', '张鸣', '中国人民大学政治学系教授、博士生导师，他个性鲜明，在嬉笑怒骂中藏着严肃的悲悯之心。社会责任感使然，张鸣以深厚的文化底蕴和深刻的人文关怀关注天下事，为历史与当下人物事件虚华假面，使真相豁然，真知毕然。<br/>\r\n主要学术著作有《辛亥：摇晃的中国》、《北洋裂变：军阀与五四》、《武夫治国梦：中国军阀势力的形成及其社会作用》、《乡土心路八十年：中国近代化过程中农民意识的变迁》、《乡村社会权利和文化结构的变迁》等；历史文化随笔有《中国心绞痛》、《直截了当的独白》、《历史的坏脾气：晚近中国的另类观察》、《历史的底稿》、《历史的空白处》等。');
INSERT INTO `writertable` VALUES ('51', '马伯庸', '马伯庸，著名作家，公认的“文字鬼才”，功底扎实，文风多变，作品广为流传。曾获朱自清散文奖和2010年人民文学奖散文奖。');
INSERT INTO `writertable` VALUES ('52', '王鼎杰', '王鼎杰，曾任某公立省重点高中历史教师三年，因痛感于基层教学之缺乏人文精神和创新理念而辞职，以自由撰稿人身份著书立说，并致力于传统史学复兴。现为中华能源基金委员会（CEFC）战略分析师，海南海洋安全与合作研究院高级研究员。主要研究领域：战略学，战争史，儒学，中国传统史学。著有《当天朝遭遇帝国：大战略视野下的鸦片战争》（重庆大学出版社，2010）。数年来在《世界军事》战略纵论专栏、《航空档案》、《现代舰船》、《战场文集》、《百家讲坛》、《信报》（香港）、《成报》（香港）、《国学新视野》（香港）、《历史月刊》（台湾）等期刊发表战略、历史、传统文化类文章近百万言。');
INSERT INTO `writertable` VALUES ('53', '[美]盖里•陶比斯', '[美]盖里•陶比斯 Gary Taubes<br/>\r\n美国最佳医学科普作者 三度获得“美国国家科学作者奖”<br/>\r\n1977年毕业于哈佛大学生理系。在学校期间，既为《发现》（Discovery）杂志的科学记者，毕业后担任《科学》（Science）杂志通讯记者，后来又成为《纽约时报》《大西洋月刊》《君子杂志》等杂志的科学专栏作者。<br/>\r\n曾被评为“美国最佳医学科普记者”，三度获得“美国国家科学作者奖”。现在，他还在加利福尼亚大学伯克利分校健康管理学院工作。著有健康医学畅销书《卡路里的功与过》《节食减肥之迷思》等。');
INSERT INTO `writertable` VALUES ('54', '武志红', '武志红，既是资深心理咨询师，又是知名的心理专栏作家。<br/>\r\n1992年考进北京大学心理学系，2001年获得心理学系临床心理学专业硕士学位，师从国内著名的心理治疗学家钱铭怡教授。2009年成立武志红心理咨询中心，已帮助大量患者恢复健康，重获幸福。<br/>\r\n曾主持《广州日报》的“健康·心理”专栏，现为《心理月刊》专栏作家，网络视频节目《跟武志红学心理学》主讲人。<br/>\r\n著有《为何爱会伤人》《为何越爱越孤独》《为何家会伤人》《解读“疯狂”》《解读绝望》《七个心理寓言》《心灵的七种兵器》《梦知道答案》等，个人作品销量超百万册。');
INSERT INTO `writertable` VALUES ('55', '斌卡', '斌卡，健身导师，硬派健身创始人。<br/>\r\n2014年2月27日，斌卡在硬派健身微信订阅号里发表第一篇文章。<br/>\r\n2014年3月27日，仅一个月，硬派健身微信订阅号关注者就突破30,000人。<br/>\r\n2014年6月4日，《硬派健身•减肥篇：知乎斌卡自选集》在各大电子商务平台上架。<br/>\r\n2014年9月，《硬派健身•减肥篇：知乎斌卡自选集》拿下亚马逊电子书季度销售榜冠军，并且至今仍在分类榜单排名第一。<br/>\r\n2014年10月，斌卡获得雷军旗下顺为资本的天使投资，开始做自己真正关心的事情。<br/>\r\n2015年5月，“硬派健身”平台在微信、知乎的粉丝突破1,000,000。');
INSERT INTO `writertable` VALUES ('56', '[美]苏珊•阿尔伯斯', '苏珊•阿尔伯斯（Susan Albers）<br/>\r\n美国著名体重管理专家，心理学家。<br/>\r\n阿尔伯斯博士是美国克利夫兰医学中心的心理医师，专长为关系和体重问题的治疗。她在美国丹佛大学获得硕士和博士学位，在印第安纳州的圣母大学完成了美国精神医疗学会的实习，并在加利福尼亚州的斯坦福大学担任博士后研究员，也是“饮食失调学会”的会员。<br/>\r\n苏珊•阿尔伯斯博士的著作被《奥普拉杂志》、《华尔街日报》、《健康》杂志、《华盛顿邮报》等评为经典。而且在美国著名的健康养生节目《奥兹医生秀》中，阿尔伯斯也是深受观众喜爱的重要嘉宾。');
INSERT INTO `writertable` VALUES ('57', '王鹏程', '王鹏程<br/>\r\n培训师，生涯规划师，阳光心态公益联盟创始人。北京大学心理学硕士，天津大学管理学硕士。<br/>\r\n曾任世界五百强企业培训经理，现任美国某著名半导体公司大中华区培训发展经理，负责中国内地、香港、台湾的员工培训与发展工作。<br/>\r\n用中英文双语授课，培训过的学员超过4000人。');
INSERT INTO `writertable` VALUES ('58', '李宗厚', '李宗厚，职场达人兼传统文化研究者。纵横职场数年，忽而动极生静，结合中国传统谋略资源，写出了这部当代人职场生存和升迁指南。');
INSERT INTO `writertable` VALUES ('59', '张一驰', '张一驰编著有《哈佛的另一种学问：读读世界一流大学的人生指导课》、 《宽容的智慧》、《你的效率是整理出来的》等书籍。');
INSERT INTO `writertable` VALUES ('60', '张玎', '张玎，曾担任北京化工大学大学生辅导员、北阳电子技术有限公司项目主管、中华英才网特聘职业规划专家、企业培训师。曾经在国内外多家企业进行培训讲座。曾是中华英才网伯乐谷首页推荐专家。<br/>\r\n张玎的又一力作——个人视频评书《职场内家拳》，曾经一度成为video CCTV．com首页推荐视频。该书现改名为《职场金刚经》，由中国工人出版社隆重推出。');
INSERT INTO `writertable` VALUES ('61', '[日]森拓郎', '森拓郎，日本著名健身教练、有氧运动教师、塑身师、美容矫正师。<br/>\r\n2009年在东京的惠比寿成立了自己的工作室“RINATO”（体能训练与有氧运动），专门从事减肥与塑身指导的工作。许多时尚模特和女演员都是工作室的常客。<br/>\r\n他的指导得到广泛的好评，受到电视、杂志等众多媒体的关注，是目前最炙手可热的塑身达人。');
INSERT INTO `writertable` VALUES ('62', '刘文辉', '刘文辉，人力资源管理硕士。长期为企事业单位提供管理咨询及培训服务。擅长企业管理与公共管理的比较研究，对企业文化、人事制度、员工职业生涯规划等领域颇有研究。著有《向华为学执行》一书。展怒放的妖娆，素心凝露，吐散微芒。不敢大乘以度人，只望小乘以度己。<br/>\r\n出版长篇小说《官戒》、《得失》等。');

-- ----------------------------
-- Procedure structure for addUserHead
-- ----------------------------
DROP PROCEDURE IF EXISTS `addUserHead`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `addUserHead`(in filename varchar(50),in userId int(11),out result int(20))
BEGIN
-- DECLARE userId varchar DEFAULT '';
update usertable set user_head=filename where user_id=userId;
set result=1;
select result;
end
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for getAllShorts
-- ----------------------------
DROP PROCEDURE IF EXISTS `getAllShorts`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `getAllShorts`(in bookId int(11),out result int(1))
BEGIN
select short_id,short_title,short_content,usertable.user_id,user_name,user_head,short_time,like_num,dislike_num,short_num
from shorttable 
LEFT JOIN usertable on usertable.user_id=shorttable.user_id
LEFT JOIN booktable on booktable.book_id=shorttable.book_id
LEFT JOIN (select count(1) short_num,book_id from shorttable group by book_id) st on booktable.book_id=st.book_id
where booktable.book_id=bookId
order by like_num desc;
set result=1;
select result;
END
;;
DELIMITER ;

