-- ----------------------------
-- Procedure structure for getMostCollectArticleByTopic
-- ----------------------------
DROP PROCEDURE IF EXISTS `getMostCollectArticleByTopic`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `getMostCollectArticleByTopic`(in topicId int(11),out result int(1))
BEGIN
select
article_com,
articletable.article_id,
usertable.user_id,
article_title,article_img,
user_name,user_head,
article_content ,
article_collect,
article_time,
topictable.topic_id,
topic_name,
topic_img,
topic_content,
topic_attent
from articletable
left join (select count(1) article_com,article_id from articlecomtable  group by article_id)artcom on articletable.article_id=artcom.article_id
left join usertable on articletable.user_id=usertable.user_id
left join topictable on topictable.topic_id=articletable.topic_id
left join (select count(1) topic_attent,topic_id from attenttable group by topic_id) atten on atten.topic_id=topictable.topic_id
left join (select count(1) article_collect,article_id from collecttable group by article_id) temp on articletable.article_id=temp.article_id

where topictable.topic_id=topicId

group by articletable.article_id 
ORDER BY article_collect desc;

set result=1;
select result;

end
;;
DELIMITER ;

