-- ----------------------------
-- Procedure structure for getUserArticle
-- ----------------------------
DROP PROCEDURE IF EXISTS `getUserArticle`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `getUserArticle`(in userId int(11),out result int(1))
BEGIN
select
coll1.collect_id,
coll1.article_id,
coll1.collect_time,
article_title,
article_content,
article_com,
article_img,
usertable.user_name,
usertable.user_head,
article_time,
topictable.topic_id,
topic_name,
article_collect
from usertable yonghu
left join collecttable coll1 on coll1.user_id=yonghu.user_id
-- 连接用户收藏的文章
left join articletable on articletable.article_id=coll1.article_id
-- 获取文章收藏数
left join (select count(1) article_collect,article_id from collecttable group by article_id) coll2 on coll2.article_id=articletable.article_id
left join (select count(1) article_com,article_id from articlecomtable group by article_id) artcom on artcom.article_id=articletable.article_id
-- 连接写文章的作者
left join usertable on articletable.user_id=usertable.user_id
left join topictable on topictable.topic_id=articletable.topic_id
where yonghu.user_id=userId
ORDER BY collect_time desc;
set result=1;
select result;
END
;;
DELIMITER ;

