-- Procedure structure for getUserBook
-- ----------------------------
DROP PROCEDURE IF EXISTS `getUserBook`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `getUserBook`(in userId int(11),out result int(1))
BEGIN
select
love1.love_id,
love1.book_id,
love_time,
book_name,
book_img,
book_content,
book_love,
bookcom_num
from usertable
left join lovetable love1 on love1.user_id=usertable.user_id
left join booktable on booktable.book_id=love1.book_id
left join (select count(1) book_love,book_id from lovetable group by book_id) love2 on love2.book_id=booktable.book_id
left join (select count(1) bookcom_num,book_id from bookcomtable group by book_id) bkcom on bkcom.book_id=love2.book_id
where usertable.user_id=userId
ORDER BY love_time desc;
set result=1;
select result;
END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for getUserTopic
-- ----------------------------
DROP PROCEDURE IF EXISTS `getUserTopic`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `getUserTopic`(in userId int(11),out result int(1))
BEGIN
select
atten1.attent_id,
atten1.topic_id,
attent_time,
topic_name,
topic_img,
topic_content,
attent_num,
article_num
from usertable
-- 用户关注多少话题
left join attenttable atten1 on atten1.user_id=usertable.user_id
-- 获取话题信息
left join topictable on topictable.topic_id=atten1.topic_id
-- 该话题有多少用户关注
left join (select count(1) attent_num,topic_id from attenttable group by topic_id) atten2 on atten2.topic_id=topictable.topic_id
-- 该话题有多少文章
left join (select count(1) article_num,topic_id from articletable group by topic_id) art on art.topic_id=atten2.topic_id
where usertable.user_id=userId
ORDER BY attent_time desc;
set result=1;
select result;
END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for new_procedure
-- ----------------------------
DROP PROCEDURE IF EXISTS `new_procedure`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `new_procedure`(in searchCon varchar(20),out result int(1))
BEGIN
select
short_id,
short_title,
short_content,
short_time,
like_num,
dislike_num,
usertable.user_id,
user_name,
user_head
from shorttable
left join usertable on shorttable.user_id=usertable.user_id
where short_title like concat('%',searchCon,'%') or short_content like concat('%',searchCon,'%')
ORDER BY like_num desc;
set result=1;
select result;
END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for searchArticle
-- ----------------------------
DROP PROCEDURE IF EXISTS `searchArticle`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `searchArticle`(in searchCon varchar(20),out result int(1))
BEGIN
select
articletable.article_id,
article_title,
article_content,
artcom_num,
article_img,
usertable.user_name,
usertable.user_head,
article_time,
topic_name,
art_collect
from articletable
-- 该文章收藏数
left join (select count(1) art_collect,article_id from collecttable group by article_id) coll on coll.article_id=articletable.article_id
-- 评论数
left join (select count(1) artcom_num,article_id from articlecomtable group by article_id) artcom on artcom.article_id=articletable.article_id
-- 连接写文章的作者
left join usertable on articletable.user_id=usertable.user_id
left join topictable on topictable.topic_id=articletable.topic_id
where article_title like concat('%',searchCon,'%') or article_content like concat('%',searchCon,'%') or usertable.user_name like concat('%',searchCon,'%') or topic_name like concat('%',searchCon,'%')
ORDER BY artcom_num desc;
set result=1;
select result;
END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for searchBook
-- ----------------------------
DROP PROCEDURE IF EXISTS `searchBook`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `searchBook`(in searchCon varchar(20),out result int(1))
BEGIN
select
booktable.book_id,
book_name,
book_img,
book_tag,
writer_name,
book_love,
writer_content,
book_content,
bookcom_num
from booktable
left join writertable on writertable.writer_id=booktable.writer_id
left join (select count(1) book_love,book_id from lovetable group by book_id) love2 on love2.book_id=booktable.book_id
left join (select count(1) bookcom_num,book_id from bookcomtable group by book_id) bkcom on bkcom.book_id=love2.book_id
where book_name like concat('%',searchCon,'%')  or book_subhead like concat('%',searchCon,'%') or book_tag like concat('%',searchCon,'%')  or writer_name like concat('%',searchCon,'%')  
ORDER BY book_love desc;
set result=1;
select result;
END
;;
DELIMITER ;

