CREATE DEFINER=`root`@`%` PROCEDURE `showBookComByReId`(in bookcomId int(11),out result int(1))
BEGIN
select bookcom_id,bookcom_content,user_head,user_name,bookcomtable.user_id,bookcomtable.book_id,bookcom_time,like_num 
from bookcomtable 
left join usertable on bookcomtable.user_id=usertable.user_id
where bookcomtable.bookcom_id=bookcomId;
set result=1;
select result;
END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for showBookComs
-- ----------------------------
DROP PROCEDURE IF EXISTS `showBookComs`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `showBookComs`(in bookId int(11),out result int(1))
BEGIN
select bookcom_id,bookcom_num,bookcom_content,user_head,user_name,bookcomtable.user_id,bookcomtable.book_id,bookcom_time,like_num 
from bookcomtable 
left join usertable on bookcomtable.user_id=usertable.user_id
left join (select count(1) bookcom_num,book_id from bookcomtable GROUP BY book_id) bc on bc.book_id=bookcomtable.book_id
where bookcomtable.book_id=bookId
order by bookcom_time desc;
set result=1;
select result;
END
;;
DELIMITER ;

