-- ----------------------------
-- Procedure structure for showUserPubColl
-- ----------------------------
DROP PROCEDURE IF EXISTS `showUserPubColl`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `showUserPubColl`(in userId int(11),out result int(1))
BEGIN
select
articletable.user_id,
count(1) article_com,
articletable.article_id,
article_title,article_img,
user_name,user_head,
article_content,
article_collect,
article_time,
topictable.topic_id,
topic_name
from articletable
left join articlecomtable on articletable.article_id=articlecomtable.article_id
left join usertable on articletable.user_id=usertable.user_id
left join topictable on topictable.topic_id=articletable.topic_id
left join (select count(1) article_collect,article_id from collecttable group by article_id) temp on articletable.article_id=temp.article_id
where usertable.user_id=userId
group by articletable.article_id 
ORDER BY article_collect desc;
set result=1;
select result;
END
;;
DELIMITER ;

