-- ----------------------------
-- Procedure structure for testaaa
-- ----------------------------
DROP PROCEDURE IF EXISTS `testaaa`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `testaaa`(in articleId int(11),out article_id int(11),out articleTitle varchar(50),out article_img varchar(100),out article_content text,out article_time varchar(20),out topic_id int(11),out topic_name varchar(20),out user_id int(11),out user_name varchar(20))
BEGIN
select
articletable.article_id,
article_title,article_img,
user_name,user_head,
article_content ,
article_time,
topictable.topic_id,
topic_name
from articletable
left join usertable on articletable.user_id=usertable.user_id
left join topictable on topictable.topic_id=articletable.topic_id
where article_id=articleId;

set articleTitle=article_title;
select articleTitle;
END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for threebooks
-- ----------------------------
DROP PROCEDURE IF EXISTS `threebooks`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `threebooks`(in bm int(11),in bn int(11))
BEGIN
select love_num,booktable.book_id,book_name,book_img 
from booktable 
left join (select count(1) love_num,book_id 
from lovetable 
group by book_id)lot on booktable.book_id=lot.book_id 
group by book_id ORDER BY love_num desc limit bm,bn;
END
;;
DELIMITER ;

-- ----------------------------
