-- ----------------------------
-- Procedure structure for topicattnum
-- ----------------------------
DROP PROCEDURE IF EXISTS `topicattnum`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `topicattnum`(in m int(11),in n int(11))
BEGIN
select topictable.topic_id,topic_name,topic_content,topic_img,article_num,attent_num
from topictable
left join (select count(1) article_num,topic_id from articletable GROUP BY topic_id) art on topictable.topic_id=art.topic_id
left join (select count(1) attent_num,topic_id from attenttable GROUP BY topic_id) att on att.topic_id=topictable.topic_id
group by topictable.topic_id
order by attent_num desc
limit m,n;
END
;;
DELIMITER ;

